async function getTotalPaymentsByLocation(
  timeFrom: string,
  timeTo: string,
  locationId: ResourceIdentifier<Location>,
  eventType: EVENT_TYPE
) {
  const locationIds = await fetchLocationChildrenIds(locationId, 'CRVS_OFFICE')

  const batchQuery = async (locationIds: string[]) => {
    const [officeLocationInChildren, locationPlaceholders] = helpers.in(
      locationIds,
      'officeLocation'
    )

    try {
      return await query<Array<{ total: number; paymentType: string }>>(
        `SELECT SUM(total) AS total
          FROM payment
        WHERE eventType = $eventType
          AND time > $timeFrom
          AND time <= $timeTo
          AND (${officeLocationInChildren})
        GROUP BY paymentType`,
        {
          placeholders: {
            timeFrom,
            timeTo,
            eventType,
            ...locationPlaceholders
          }
        }
      )
    } catch (error) {
      logger.error(
        `Error fetching total payments by location: ${error.message}`
      )
      throw error
    }
  }

async function getAdvancedSearchByClient(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  try {
    const clientId = getClientIdFromToken(request.headers.authorization)
    const totalSearchResult = await fetchTotalSearchRequestByClientId(clientId)
    const total = totalSearchResult.length > 0 ? totalSearchResult[0].count : 0
    return h.response({ total }).code(200)
  } catch (err) {
    return internal(err)
  }
}

async function postAdvancedSearchByClient(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  try {
    const clientId = getClientIdFromToken(request.headers.authorization)
    const ipAddress = request.headers['x-real-ip'] || request.info.remoteAddress
    const point: IPoints = {
      fields: { clientId },
      measurement: 'search_requests',
      tags: { ipAddress },
      timestamp: undefined
    }
    await writePoints([point])
    return h.response({}).code(200)
  } catch (err) {
    return internal(err)
  }
}

async function locationStatisticsHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const locationId = request.query[LOCATION_ID]
    ? 'Location/' + request.query[LOCATION_ID]
    : undefined
  const populationYear = request.query[POPULATION_YEAR]
  const authHeader: IAuthHeader = {
    Authorization: request.headers.authorization,
    'x-correlation-id': request.headers['x-correlation-id']
  }

  return getLocationStatistics(locationId, populationYear, authHeader)
}

function getClientIdFromToken(token: string) {
  const payload = getTokenPayload(token)
  return payload.sub
}

async function fetchTotalSearchRequestByClientId(clientId: string) {
  const currentDate = format(new Date(), 'yyyy-MM-dd')
  return await query(
    `SELECT COUNT(clientId) FROM search_requests WHERE clientId = $clientId AND time >= $currentDate`,
    {
      placeholders: {
        clientId,
        currentDate
      }
    }
  )
}

async function getMeasurementNames() {
  const points = await query<Array<{ key: string }>>('SHOW SERIES')
  return points.map(({ key }) => key.split(',')[0])
}

async function exportHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const measurements = await getMeasurementNames()

  const csvStreams = []
  for (const measurement of measurements) {
    csvStreams.push([measurement, await getCSV(measurement)])
  }

  // Getting CSV contents as zip
  return archiveStreams(csvStreams)
}

async function monthlyExportHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const event =
    request.query[EVENT].toUpperCase() === 'BIRTH' ? 'Birth' : 'Death'
  const auth = request.auth as Hapi.RequestAuth & {
    token: string
  }

  const monthlyMetrics = await metricsHandler(request, h)

  const EXPECTED_BIRTH_REGISTRATION_IN_DAYS = await getRegistrationTargetDays(
    event,
    auth.token
  )

  const csvStreams = []
  // populating csv for gender based registration data
  if (monthlyMetrics.genderBasisMetrics) {
    const stream = []
    for (const genderBaseData of monthlyMetrics.genderBasisMetrics) {
      const loc = await fetchLocation(genderBaseData.location, {
        Authorization: `Bearer ${auth.token}`
      })
      stream.push({
        Location: loc.name,
        'Male Under 18': `${genderBaseData.maleUnder18} (${getPercentage(
          genderBaseData.maleUnder18,
          genderBaseData.total
        )}%)`,
        'Female Under 18': `${genderBaseData.femaleUnder18} (${getPercentage(
          genderBaseData.femaleUnder18,
          genderBaseData.total
        )}%)`,
        'Male Over 18': `${genderBaseData.maleOver18} (${getPercentage(
          genderBaseData.maleOver18,
          genderBaseData.total
        )}%)`,
        'Female Over 18': `${genderBaseData.femaleOver18} (${getPercentage(
          genderBaseData.femaleOver18,
          genderBaseData.total
        )}%)`,
        Total: genderBaseData.total
      })
    }
    csvStreams.push([
      `${event} Registered`,
      stringify(stream, { header: true })
    ])
  }
  // populating csv for time frame based registration data
  if (monthlyMetrics.timeFrames) {
    const stream = []
    for (const timeFrameData of monthlyMetrics.timeFrames) {
      const loc = await fetchLocation(timeFrameData.locationId, {
        Authorization: `Bearer ${auth.token}`
      })
      stream.push({
        Location: loc.name,
        [`Within ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS} days`]: `${
          timeFrameData.regWithinTargetd
        } (${getPercentage(
          timeFrameData.regWithinTargetd,
          timeFrameData.total
        )}%)`,
        [`${EXPECTED_BIRTH_REGISTRATION_IN_DAYS} days - 1 year`]: `${
          timeFrameData.regWithinTargetdTo1yr
        } (${getPercentage(
          timeFrameData.regWithinTargetdTo1yr,
          timeFrameData.total
        )}%)`,
        '1 year to 5 years': `${
          timeFrameData.regWithin1yrTo5yr
        } (${getPercentage(
          timeFrameData.regWithin1yrTo5yr,
          timeFrameData.total
        )}%)`,
        'Over 5 years': `${timeFrameData.regOver5yr} (${getPercentage(
          timeFrameData.regOver5yr,
          timeFrameData.total
        )}%)`,
        Total: timeFrameData.total
      })
    }
    csvStreams.push([
      `${event} registered by time period`,
      stringify(stream, { header: true })
    ])
  }
  // populating csv for 45 days based estimated data
  if (monthlyMetrics.estimatedTargetDayMetrics) {
    const stream = []
    for (const estimatedTargetDayData of monthlyMetrics.estimatedTargetDayMetrics) {
      const loc = await fetchLocation(estimatedTargetDayData.locationId, {
        Authorization: `Bearer ${auth.token}`
      })
      stream.push({
        Location: loc.name,
        'Estimated no. of registrations':
          estimatedTargetDayData.estimatedRegistration,
        [`Total registered in ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS} days`]:
          estimatedTargetDayData.registrationInTargetDay,
        'Percentage of estimate': `${estimatedTargetDayData.estimationPercentage}%`
      })
    }
    csvStreams.push([
      `Estimated vs total registered in ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS} days`,
      stringify(stream, { header: true })
    ])
  }
  // populating csv for registration payment data
  if (monthlyMetrics.payments) {
    const stream = []
    for (const paymentData of monthlyMetrics.payments) {
      const loc = await fetchLocation(paymentData.locationId, {
        Authorization: `Bearer ${auth.token}`
      })
      stream.push({
        Location: loc.name,
        Total: paymentData.total
      })
    }
    csvStreams.push([
      `Payment collected for ${event.toLowerCase()} certificates`,
      stringify(stream, { header: true })
    ])
  }

  // Getting CSV contents as zip
  return archiveStreams(csvStreams)
}

function archiveStreams(csvStreams: any[]) {
  const archive = archiver('zip', {
    // Sets the compression level.
    zlib: { level: 9 }
  })
  csvStreams.forEach(([name, stream]) => {
    archive.append(stream as any, { name: `${name}.csv` })
  })
  archive.finalize()

  return archive
}

function getPercentage(value: number, total: number) {
  return value === 0 || total === 0 ? 0 : Math.round((value / total) * 100)
}

async function fetchKeyFigures(
  timeStart: string,
  timeEnd: string,
  location: Location,
  authHeader: IAuthHeader
) {
  const estimatedFigureForTargetDays = await fetchEstimateByLocation(
    location,
    EVENT_TYPE.BIRTH,
    authHeader,
    timeStart,
    timeEnd
  )

  const keyFigures: IBirthKeyFigures[] = []
  const queryLocationId =
    `Location/${estimatedFigureForTargetDays.locationId}` as `Location/${UUID}`
  const locationIds = await fetchLocationChildrenIds(
    queryLocationId,
    'CRVS_OFFICE'
  )
  const [officeLocationInChildren, locationPlaceholders] = helpers.in(
    locationIds,
    'officeLocation'
  )

  const EXPECTED_BIRTH_REGISTRATION_IN_DAYS = await getRegistrationTargetDays(
    EVENT_TYPE.BIRTH,
    authHeader.Authorization
  )

  /* Populating < 45D data */
  const withinTargetDaysData: IGroupedByGender[] = await query(
    `SELECT COUNT(ageInDays) AS total
      FROM birth_registration
    WHERE time >= ${timeStart}
      AND time <= ${timeEnd}
      AND (${officeLocationInChildren})
      AND ageInDays <= ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS}
    GROUP BY gender`,
    {
      placeholders: {
        ...locationPlaceholders
      }
    }
  )
  const WITHIN_TARGET_DAYS = `DAYS_0_TO_${EXPECTED_BIRTH_REGISTRATION_IN_DAYS}`
  const WITHIN_TARGET_DAYS_TO_1_YEAR = `DAYS_${
    EXPECTED_BIRTH_REGISTRATION_IN_DAYS + 1
  }_TO_365`
  keyFigures.push(
    populateBirthKeyFigurePoint(
      WITHIN_TARGET_DAYS,
      withinTargetDaysData,
      estimatedFigureForTargetDays.totalEstimation
    )
  )
  /* Populating > 45D and < 365D data */
  const estimatedFigureFor1Year = await fetchEstimateByLocation(
    location,
    EVENT_TYPE.BIRTH,
    authHeader,
    timeStart,
    timeEnd
  )
  const within1YearData: IGroupedByGender[] = await query(
    `SELECT COUNT(ageInDays) AS total
      FROM birth_registration
    WHERE time >= ${timeStart}
      AND time <= ${timeEnd}
      AND (${officeLocationInChildren})
      AND ageInDays > ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS}
      AND ageInDays <= 365
    GROUP BY gender`,
    {
      placeholders: {
        ...locationPlaceholders
      }
    }
  )
  keyFigures.push(
    populateBirthKeyFigurePoint(
      WITHIN_TARGET_DAYS_TO_1_YEAR,
      within1YearData,
      estimatedFigureFor1Year.totalEstimation
    )
  )
  /* Populating < 365D data */
  let fullData: IGroupedByGender[] = []
  if (withinTargetDaysData) {
    fullData = fullData.concat(withinTargetDaysData)
  }
  if (within1YearData) {
    fullData = fullData.concat(within1YearData)
  }
  keyFigures.push(
    populateBirthKeyFigurePoint(
      WITHIN_1_YEAR,
      fullData,
      estimatedFigureFor1Year.totalEstimation
    )
  )
  return keyFigures
}

async function fetchGenderBasisMetrics(
  timeFrom: string,
  timeTo: string,
  currLocation: string,
  currLocationLevel: string,
  locationLevel: string,
  event: EVENT_TYPE,
  childLocationIds: Array<string>
) {
  let queryString = ''
  if (event === EVENT_TYPE.BIRTH) {
    queryString = birthGenderBasisMetricsQuery(
      timeFrom,
      timeTo,
      currLocation,
      currLocationLevel,
      locationLevel
    )
  } else if (event === EVENT_TYPE.DEATH) {
    queryString = deathGenderBasisMetricsQuery(
      timeFrom,
      timeTo,
      currLocation,
      currLocationLevel,
      locationLevel
    )
  }

  const points = await query(queryString, {
    placeholders: {
      timeFrom,
      timeTo,
      currLocation
    }
  })

  const dataFromInflux = populateGenderBasisMetrics(points, locationLevel)
  const placeholder = {
    total: 0,
    maleOver18: 0,
    maleUnder18: 0,
    femaleOver18: 0,
    femaleUnder18: 0
  }

  const emptyData = childLocationIds.map((id) => ({
    location: id,
    ...placeholder
  }))

  const genderBasisData = fillEmptyDataArrayByKey(
    dataFromInflux,
    emptyData,
    'location'
  )
  return genderBasisData
}

async function fetchEstimatedTargetDayMetrics(
  timeFrom: string,
  timeTo: string,
  currLocation: string,
  currLocationLevel: string,
  locationLevel: string,
  event: EVENT_TYPE,
  childLocationIds: Array<string>,
  authHeader: IAuthHeader,
  registrationTargetInDays: number
) {
  const measurement =
    event === EVENT_TYPE.BIRTH ? 'birth_registration' : 'death_registration'
  const column = event === EVENT_TYPE.BIRTH ? 'ageInDays' : 'deathDays'
  const EXPECTED_BIRTH_REGISTRATION_IN_DAYS = registrationTargetInDays

  const points = await query(
    `SELECT
                              COUNT(${column}) AS withInTargetDay
                              FROM ${measurement}
                              WHERE ${column} <= ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS}
                              AND time > $timeFrom
                              AND time <= $timeTo
                              AND ${currLocationLevel}=$currLocation
                              GROUP BY ${locationLevel}`,
    {
      placeholders: {
        timeFrom,
        timeTo,
        currLocation
      }
    }
  )
  const dataFromInflux: IRegistrationInTargetDayEstimation[] = []
  for (const point of points) {
    const estimationOfTargetDay: IEstimation =
      await fetchEstimateForTargetDaysByLocationId(
        point[locationLevel],
        event,
        authHeader,
        timeFrom,
        timeTo
      )
    dataFromInflux.push({
      locationId: point[locationLevel],
      registrationInTargetDay: point.withInTargetDay,
      estimatedRegistration: estimationOfTargetDay.totalEstimation,
      estimationLocationLevel: estimationOfTargetDay.locationLevel,
      estimationPercentage:
        point.withInTargetDay === 0 ||
        estimationOfTargetDay.totalEstimation === 0
          ? 0
          : Number(
              (
                (point.withInTargetDay /
                  estimationOfTargetDay.totalEstimation) *
                100
              ).toFixed(2)
            )
    })
  }

  const emptyEstimationData: IRegistrationInTargetDayEstimation[] = []
  for (const id of childLocationIds) {
    const estimationOfTargetDay: IEstimation =
      await fetchEstimateForTargetDaysByLocationId(
        id,
        event,
        authHeader,
        timeFrom,
        timeTo
      )
    emptyEstimationData.push({
      locationId: id,
      registrationInTargetDay: 0,
      estimatedRegistration: estimationOfTargetDay.totalEstimation,
      estimationLocationLevel: estimationOfTargetDay.locationLevel,
      estimationPercentage: 0
    })
  }

  const estimatedTargetDayData = fillEmptyDataArrayByKey(
    dataFromInflux,
    emptyEstimationData,
    'locationId'
  )
  return estimatedTargetDayData
}

async function getTotalNumberOfRegistrations(
  timeFrom: string,
  timeTo: string,
  locationId: ResourceIdentifier<FhirLocation>,
  event: EVENT_TYPE
) {
  const locationIds = await fetchLocationChildrenIds(locationId, 'CRVS_OFFICE')
  const [officeLocationInChildren, locationPlaceholders] = helpers.in(
    locationIds,
    'officeLocation'
  )
  const measurement =
    event === EVENT_TYPE.BIRTH ? 'birth_registration' : 'death_registration'
  const totalRegistrationPoint: Registration[] = await query(
    `SELECT COUNT(DISTINCT(compositionId)) AS total
      FROM ${measurement}
    WHERE time > $timeFrom
      AND time <= $timeTo
      AND (${officeLocationInChildren})`,
    {
      placeholders: {
        timeFrom,
        timeTo,
        ...locationPlaceholders
      }
    }
  )
  return totalRegistrationPoint?.[0]?.total ?? 0
}

async function fetchLocationWiseEventEstimations(
  timeFrom: string,
  timeTo: string,
  locationId: ResourceIdentifier<FhirLocation>,
  event: EVENT_TYPE,
  authHeader: IAuthHeader
) {
  const locationIds = await fetchLocationChildrenIds(locationId, 'CRVS_OFFICE')
  const [officeLocationInChildren, locationPlaceholders] = helpers.in(
    locationIds,
    'officeLocation'
  )
  const measurement =
    event === EVENT_TYPE.BIRTH ? 'birth_registration' : 'death_registration'
  const column = event === EVENT_TYPE.BIRTH ? 'ageInDays' : 'deathDays'
  const EXPECTED_BIRTH_REGISTRATION_IN_DAYS = await getRegistrationTargetDays(
    event,
    authHeader.Authorization
  )
  const registrationsInTargetDaysPoints: IGroupedByGender[] = await query(
    `SELECT COUNT(${column}) AS total
      FROM ${measurement}
    WHERE time > $timeFrom
      AND time <= $timeTo
      AND (${officeLocationInChildren})
      AND ${column} <= ${EXPECTED_BIRTH_REGISTRATION_IN_DAYS}
    GROUP BY gender`,
    {
      placeholders: {
        timeFrom,
        timeTo,
        ...locationPlaceholders
      }
    }
  )

  let totalRegistrationInTargetDay = 0
  let totalMaleRegistrationInTargetDay = 0
  let totalFemaleRegistrationInTargetDay = 0
  registrationsInTargetDaysPoints.forEach((point) => {
    totalRegistrationInTargetDay += point.total
    if (point.gender === 'male') {
      totalMaleRegistrationInTargetDay += point.total
    } else if (point.gender === 'female') {
      totalFemaleRegistrationInTargetDay += point.total
    }
  })
  const estimationOfTargetDay: IEstimation =
    await fetchEstimateForTargetDaysByLocationId(
      locationId,
      event,
      authHeader,
      timeFrom,
      timeTo
    )

  return {
    actualRegistration: totalRegistrationInTargetDay,
    estimatedRegistration: estimationOfTargetDay.totalEstimation,
    estimatedPercentage:
      totalRegistrationInTargetDay === 0 ||
      estimationOfTargetDay.totalEstimation === 0
        ? 0
        : Number(
            (
              (totalRegistrationInTargetDay /
                estimationOfTargetDay.totalEstimation) *
              100
            ).toFixed(2)
          ),
    malePercentage:
      totalMaleRegistrationInTargetDay === 0 ||
      estimationOfTargetDay.maleEstimation === 0
        ? 0
        : Number(
            (
              (totalMaleRegistrationInTargetDay /
                estimationOfTargetDay.maleEstimation) *
              100
            ).toFixed(2)
          ),
    femalePercentage:
      totalFemaleRegistrationInTargetDay === 0 ||
      estimationOfTargetDay.femaleEstimation === 0
        ? 0
        : Number(
            (
              (totalFemaleRegistrationInTargetDay /
                estimationOfTargetDay.femaleEstimation) *
              100
            ).toFixed(2)
          )
  }
}

async function fetchLocaitonWiseEventEstimationsGroupByTimeLabel(
  timeFrom: string,
  timeTo: string,
  locationId: string,
  event: EVENT_TYPE,
  authHeader: IAuthHeader
) {
  const measurement =
    event === EVENT_TYPE.BIRTH ? 'birth_registration' : 'death_registration'
  const column = event === EVENT_TYPE.BIRTH ? 'ageInDays' : 'deathDays'

  const registrations: IGroupByTimeLabel[] = await query(
    `SELECT COUNT(${column}) AS total
      FROM ${measurement}
    WHERE time > $timeFrom
      AND time <= $timeTo
      AND officeLocation = $locationId
    GROUP BY timeLabel`,
    {
      placeholders: {
        timeFrom,
        timeTo,
        locationId
      }
    }
  )
  const estimationOfTimeRange: IEstimation =
    await fetchEstimateForTargetDaysByLocationId(
      locationId,
      event,
      authHeader,
      timeFrom,
      timeTo
    )

  return {
    estimated: estimationOfTimeRange,
    results: registrations
  }
}

async function fetchEventsGroupByMonthDatesByLocation(
  timeFrom: string,
  timeTo: string,
  locationId: ResourceIdentifier<FhirLocation>,
  event: EVENT_TYPE
) {
  const measurement =
    event === EVENT_TYPE.BIRTH ? 'birth_registration' : 'death_registration'
  const column = event === EVENT_TYPE.BIRTH ? 'ageInDays' : 'deathDays'
  const locationIds = await fetchLocationChildrenIds(locationId, 'CRVS_OFFICE')

  const batchquery = async (locationIds: string[]) => {
    const [officeLocationInChildren, locationPlaceholders] = helpers.in(
      locationIds,
      'officeLocation'
    )
    try {
      const registrationsInTargetDaysPoints: IGroupByEventDate[] = await query(
        `SELECT COUNT(${column}) AS total
          FROM ${measurement}
        WHERE time > $timeFrom
          AND time <= $timeTo
          AND (${officeLocationInChildren})
        GROUP BY dateLabel, timeLabel`,
        {
          placeholders: {
            timeFrom,
            timeTo,
            ...locationPlaceholders
          }
        }
      )

      return registrationsInTargetDaysPoints
    } catch (error) {
      logger.error(
        `Error fetching events group by month dates by location: ${error.message}`
      )
      throw error
    }
  }

  const locationBatches = createChunks(locationIds, 1000)
  return await Promise.all(locationBatches.map(batchquery)).then((res) =>
    res.flat()
  )
