  const results: any[] = []

  months.forEach((month) => {
    const filteredRegistrations = registrations.filter(
      (registration) => registration.month === month
    )

    const lateRegistrations = filteredRegistrations.filter(
      (result) => result.timeLabel === 'withinLate'
    )
