async function getTotalCorrectionsByLocation(
  timeFrom: string,
  timeTo: string,
  locationId: ResourceIdentifier<Location>,
  event: EVENT_TYPE
) {
  const locationIds = await fetchLocationChildrenIds(locationId, 'CRVS_OFFICE')

  const batchQuery = async (locationIds: string[]) => {
    const [officeLocationInChildren, locationPlaceholders] = helpers.in(
      locationIds,
      'officeLocation'
    )

    const q = `SELECT COUNT(compositionId) AS total
                FROM correction
              WHERE time > $timeFrom
                AND time <= $timeTo
                AND eventType = $event
                AND (${officeLocationInChildren})
              GROUP BY reason`

    try {
      const totalCorrections: ICorrectionTotalGroup[] = await query(q, {
        placeholders: {
          timeFrom,
          timeTo,
          event,
          ...locationPlaceholders
        }
      })
      return totalCorrections
    } catch (error) {
      logger.error(
        `Error fetching total corrections by location: ${error.message}`
      )
      throw error
    }
  }

  const locationBatches = createChunks(locationIds, 1000)
  return await Promise.all(locationBatches.map(batchQuery)).then((res) =>
    res.flat()
  )
}

async function getForm(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const token = request.headers.authorization
  const url = new URL('forms', env.COUNTRY_CONFIG_URL)
  const response = await fetch(url, {
    headers: {
      Authorization: token
    }
  })

  if (response.status !== 200) {
    logger.error(
      `Core failed to fetch form definition from ${url.href}. Check country config logs for more details`
    )

    return h.response().code(500)
  }

  const forms: IFormsPayload = await response.json()

  if (process.env.NODE_ENV === 'development') {
    const result = registrationForms.safeParse(forms)

    if (!result.success) {
      throw badData(
        fromZodError(result.error, {
          prefix: 'Form validation error',
          maxIssuesInMessage: 5
        }).message
      )
    }
  }

  const formVersion: IFormVersionModel | null = await FormVersions.findOne({
    version: forms.version
  })
  if (!formVersion) {
    try {
      await FormVersions.create({
        birthForm: JSON.stringify(forms.birth),
        deathForm: JSON.stringify(forms.death),
        marriageForm: JSON.stringify(forms.marriage),
        version: forms.version,
        status: Status.ACTIVE
      })
    } catch (err) {
      logger.error(err)
      // return 400 if there is a validation error when saving to mongo
      return h.response().code(400)
    }
  }
  return h.response(forms).code(200)
}

