async function verifyUserHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
): Promise<IVerifyUserResponse> {
  const payload = request.payload as IVerifyUserPayload
  let result
  try {
    result = await verifyUser(payload.mobile, payload.email)
  } catch (err) {
    throw unauthorized()
  }
  const nonce = generateNonce()
  const isUserNameRetrievalFlow =
    payload.retrieveFlow.toLowerCase() === RETRIEVAL_FLOW_USER_NAME

  await storeRetrievalStepInformation(
    nonce,
    isUserNameRetrievalFlow
      ? RetrievalSteps.NUMBER_VERIFIED
      : RetrievalSteps.WAITING_FOR_VERIFICATION,
    result
  )

  if (!isUserNameRetrievalFlow) {
    const notificationEvent = NotificationEvent.PASSWORD_RESET

    await generateAndSendVerificationCode(
      nonce,
      result.scope,
      notificationEvent,
      result.userFullName,
      result.mobile,
      result.email
    )
  }

  const response: IVerifyUserResponse = {
    nonce
  }
  if (isUserNameRetrievalFlow) {
    response.securityQuestionKey = result.securityQuestionKey
  }
  return response
}

function getSectionBySectionCode(
  bundle: fhir.Bundle,
  sectionCode: string
): fhir.Patient {
  const composition: fhir.Composition = getResourceByType(
    bundle,
    FHIR_RESOURCE_TYPE.COMPOSITION
  ) as fhir.Composition
  const personSection =
    composition &&
    composition.section &&
    composition.section.find((section: fhir.CompositionSection) => {
      if (!section.code || !section.code.coding || !section.code.coding.some) {
        return false
      }
      return section.code.coding.some((coding) => coding.code === sectionCode)
    })

  if (!personSection || !personSection.entry) {
    throw new Error(`No section found for given code: ${sectionCode}`)
  }
  const sectionRef = personSection.entry[0].reference
  const personEntry =
    bundle.entry &&
    bundle.entry.find((entry: any) => entry.fullUrl?.includes(sectionRef))

  if (!personEntry) {
    throw new Error(
      'Patient referenced from composition section not found in FHIR bundle'
    )
  }
  return personEntry.resource as fhir.Patient
}

function isTaskResource(resource: fhir.Resource): resource is fhir.Task {
  return resource.resourceType === 'Task'
}

function findPreviousTask(
  historyResponseBundle: fhir.Bundle,
  allowedPreviousStates: DECLARATION_STATUS[]
) {
  const allTasks = findAllPreviousTasks(historyResponseBundle)

  const task =
    allTasks &&
    allTasks.find((resource) => {
      if (!resource.businessStatus || !resource.businessStatus.coding) {
        return false
      }
      return resource.businessStatus.coding.some((coding) =>
        allowedPreviousStates.includes(coding.code as DECLARATION_STATUS)
      )
    })

  if (!task) {
    return null
  }
  return task as fhir.Task
}

function findAllPreviousTasks(historyResponseBundle: fhir.Bundle) {
  const task =
    historyResponseBundle.entry &&
    historyResponseBundle.entry
      .map((entry) => entry.resource)
      .filter((resource): resource is fhir.Task =>
        Boolean(resource && isTaskResource(resource))
      )

  if (!task) {
    return null
  }
  return task as fhir.Task[]
}

function getPaymentReconciliation(
  bundle: fhir.Bundle,
  task?: fhir.Task
) {
  if (task) {
    const paymentDetailsReference = task.extension?.find((x) =>
      x.url.includes('paymentDetails')
    )?.valueReference?.reference

    const paymentReconciliation = bundle.entry?.find(
      (x) =>
        x.fullUrl &&
        (isURLReference(x.fullUrl)
          ? urlReferenceToResourceIdentifier(x.fullUrl as URLReference)
          : x.fullUrl) === paymentDetailsReference
    )?.resource

    if (paymentReconciliation)
      return paymentReconciliation as fhir.PaymentReconciliation
  }

  return getResourceByType<fhir.PaymentReconciliation>(
    bundle,
    FHIR_RESOURCE_TYPE.PAYMENT_RECONCILIATION
  )
}

export function getTask(bundle: fhir.Bundle) {
  return getResourceByType<fhir.Task>(bundle, FHIR_RESOURCE_TYPE.TASK)
}
export function getDownloadedExtensionStatus(task: fhir.Task) {
  const extension =
    task.extension && findExtension(DOWNLOADED_EXTENSION_URL, task.extension)
  return extension?.valueString
}

export function findExtension(
  url: string,
  extensions: fhir.Extension[]
): fhir.Extension | undefined {
  const extension =
    extensions &&
    extensions.find((obj: fhir.Extension) => {
      return obj.url === url
    })
  return extension
}

export function getComposition(bundle: fhir.Bundle) {
  return getResourceByType<fhir.Composition>(
    bundle,
    FHIR_RESOURCE_TYPE.COMPOSITION
  )
}

export function getCompositionIdFromCompositionOrTask(bundle: fhir.Bundle) {
  const composition = getComposition(bundle)

  if (composition) {
    return composition.id
  }

  const task = getTask(bundle)
  return task?.focus?.reference?.split('/')[1]
}

export function hasRequestCorrectionExtension(task: fhir.Task) {
  const extension =
    task.extension &&
    findExtension(MAKE_CORRECTION_EXTENSION_URL, task.extension)
  return extension
}

export async function getPreviousTask(
  task: fhir.Task,
  allowedPreviousStates: DECLARATION_STATUS[],
  authHeader: IAuthHeader
) {
  const taskHistory = await fetchTaskHistory(task.id!, authHeader)
  return findPreviousTask(taskHistory, allowedPreviousStates)
}

export function getPractitionerIdFromBundle(bundle: fhir.Bundle) {
  const task = getTask(bundle)
  if (!task) {
    throw new Error('Task not found in bundle')
  }
  return getPractionerIdFromTask(task)
}

export function getActionFromTask(task: fhir.Task) {
  const businessStatus = getStatusFromTask(task)
  const extensionStatusWhileDownloaded = getDownloadedExtensionStatus(task)
  if (businessStatus === extensionStatusWhileDownloaded) {
    return GQLRegStatus.DOWNLOADED
  } else if (hasRequestCorrectionExtension(task)) {
    return GQLRegStatus.REQUESTED_CORRECTION
  }

if (task) {
    const paymentDetailsReference = task.extension?.find((x) =>
      x.url.includes('paymentDetails')
    )?.valueReference?.reference

    const paymentReconciliation = bundle.entry?.find(
      (x) =>
        x.fullUrl &&
        (isURLReference(x.fullUrl)
          ? urlReferenceToResourceIdentifier(x.fullUrl as URLReference)
          : x.fullUrl) === paymentDetailsReference
    )?.resource

    if (paymentReconciliation)
      return paymentReconciliation as fhir.PaymentReconciliation
  }

function getStatusFromTask(task: fhir.Task) {
  const statusType = task.businessStatus?.coding?.find(
    (coding: fhir.Coding) =>
      coding.system === `${OPENCRVS_SPECIFICATION_URL}reg-status`
  )
  return statusType && statusType.code
}

function getPractionerIdFromTask(task: fhir.Task) {
  return task?.extension
    ?.find(
      (ext) => ext.url === 'http://opencrvs.org/specs/extension/regLastUser'
    )
    ?.valueReference?.reference?.split('/')?.[1]
}

function getDeclarationStatus(
  task: fhir.Task
): DECLARATION_STATUS | null {
  if (!task.businessStatus || !task.businessStatus.coding) {
    return null
  }

  const coding = task.businessStatus.coding.find(
    ({ system }) => system === 'http://opencrvs.org/specs/reg-status'
  )
  if (!coding) {
    return null
  }
  return coding.code as DECLARATION_STATUS
}

function getTrackingId(task: fhir.Task) {
  const trackingIdentifier = task?.identifier?.find((identifier) => {
    return (
      identifier.system === `http://opencrvs.org/specs/id/birth-tracking-id` ||
      identifier.system === `http://opencrvs.org/specs/id/death-tracking-id` ||
      identifier.system === `http://opencrvs.org/specs/id/marriage-tracking-id`
    )
  })
  if (!trackingIdentifier || !trackingIdentifier.value) {
    throw new Error("Didn't find any identifier for tracking id")
  }
  return trackingIdentifier.value
}

function getDeclarationType(task: fhir.Task): DECLARATION_TYPE {
  const coding = task.code?.coding?.find(
    ({ system }) => system === 'http://opencrvs.org/specs/types'
  )
  if (!coding) {
    throw new Error('No declaration type found in task')
  }
  return coding.code as DECLARATION_TYPE
}

function getRecordInitiator(taskHistory: fhir.Bundle): string {
  const allowedPreviousStates = [
    'DECLARED',
    'VALIDATED',
    'IN_PROGRESS',
    'WAITING_VALIDATION'
  ]
  const previousTasks = findAllPreviousTasks(taskHistory)

  const task =
    previousTasks &&
    previousTasks
      .sort((a, b) => {
        if (!a.meta?.lastUpdated || !b.meta?.lastUpdated) {
          return -1
        }
        if (a.meta.lastUpdated > b.meta.lastUpdated) {
          return 1
        }
        if (a.meta.lastUpdated < b.meta.lastUpdated) {
          return -1
        }
        return 0
      })
      .find((resource) => {
        if (!resource.businessStatus || !resource.businessStatus.coding) {
          return false
        }
        return resource.businessStatus.coding.some((coding) =>
          allowedPreviousStates.includes(coding.code as DECLARATION_STATUS)
        )
      })

  if (!task) {
    throw new Error('Task not found!')
  }

  const regLastUserExtension =
    task &&
    task.extension &&
    task.extension.find(
      (extension) =>
        extension.url === 'http://opencrvs.org/specs/extension/regLastUser'
    )

  const regLastUser =
    regLastUserExtension &&
    regLastUserExtension.valueReference &&
    (regLastUserExtension.valueReference.reference as string)

  return regLastUser?.split('/')?.[1] || ''
}

function getRegLastOffice(bundle: fhir.Bundle) {
  const task: fhir.Task = getResourceByType(
    bundle,
    FHIR_RESOURCE_TYPE.TASK
  ) as fhir.Task
  if (!task) {
    throw new Error('Task not found!')
  }
  const regLastOffice =
    task.extension &&
    task.extension.find(
      (extension) =>
        extension.url === 'http://opencrvs.org/specs/extension/regLastOffice'
    )

  return (
    regLastOffice &&
    regLastOffice.valueReference &&
    regLastOffice.valueReference.reference
  )
}

async function getEncounterLocationType(
  bundle: fhir.Bundle,
  authHeader: IAuthHeader
) {
  const encounter = getResourceByType<fhir.Encounter>(
    bundle,
    FHIR_RESOURCE_TYPE.ENCOUNTER
  )

  if (!encounter) {
    throw new Error('Encounter not found!')
  }

  const locationId = encounter.location?.[0].location.reference
  if (!locationId) {
    throw new Error('Encounter location not found!')
  }

  const location = await fetchLocation(locationId, authHeader)
  if (!location || !location.type) {
    throw new Error(
      `Encounter location not found from Hearth with id ${locationId}!`
    )
  }
  const type = location.type.coding?.[0]?.code

  if (!type) {
    throw new Error(
      `Encounter location was found from Hearth with id ${locationId}, but the location did not have a proper type code`
    )
  }
  return type
}

function getTimeLoggedFromTask(task: fhir.Task) {
  if (!task.extension) {
    throw new Error(`Task has no extensions defined, task ID: ${task.id}`)
  }

  const timeLoggedExt = task.extension.find(
    (ext) => ext.url === 'http://opencrvs.org/specs/extension/timeLoggedMS'
  )

  if (!timeLoggedExt || timeLoggedExt.valueInteger === undefined) {
    throw new Error(
      `No time logged extension found in task, task ID: ${task.id}`
    )
  }

  return timeLoggedExt.valueInteger
}

function isNotification(composition: fhir.Composition): boolean {
  const compositionTypeCode =
    composition.type.coding &&
    composition.type.coding.find(
      (code) => code.system === 'http://opencrvs.org/doc-types'
    )
  if (!compositionTypeCode) {
    throw new Error('Composition has no type codings defined')
  }

  return (
    (compositionTypeCode.code &&
      NOTIFICATION_TYPES.includes(compositionTypeCode.code)) ||
    false
  )
}

function getObservationValueByCode(
  bundle: fhir.Bundle,
  observationCode: string
): string {
  const observationBundle =
    bundle.entry &&
    bundle.entry.filter((item) => {
      return (
        item && item.resource && item.resource.resourceType === 'Observation'
      )
    })
  if (!Array.isArray(observationBundle) || !observationBundle.length) {
    return 'UNKNOWN'
  }
  const selectedObservationEntry = observationBundle.find((entry) => {
    const observationEntry = entry.resource as fhir.Observation
    return (
      (observationEntry.code &&
        observationEntry.code.coding &&
        observationEntry.code.coding[0] &&
        observationEntry.code.coding[0].code === observationCode) ||
      null
    )
  })
  if (!selectedObservationEntry) {
    return 'UNKNOWN'
  }
  const observationResource =
    selectedObservationEntry.resource as fhir.Observation
  const value =
    (observationResource.valueCodeableConcept &&
      observationResource.valueCodeableConcept.coding &&
      observationResource.valueCodeableConcept.coding[0] &&
      observationResource.valueCodeableConcept.coding[0].code) ||
    'UNKNOWN'

  return value
}

async function fetchDeclarationsBeginnerRole(
  fhirBundle: fhir.Bundle,
  authHeader: IAuthHeader
) {
  let startedByRole = ''
  const currentTask = getTask(fhirBundle)

  if (currentTask) {
    const bundle = await fetchTaskHistory(currentTask.id!, authHeader)
    const length = bundle.entry ? bundle.entry.length : 0
    const task =
      bundle.entry &&
      bundle.entry
        .map((entry) => entry.resource)
        .filter((resource): resource is fhir.Task =>
          Boolean(resource && isTaskResource(resource))
        )

    if (task && length > 0) {
      const startedTask = task[length - 1] //the last task in the entries of history bundle
      const practitionerId = getPractionerIdFromTask(startedTask)
      if (!practitionerId) {
        throw new Error('Practitioner id not found')
      }
      startedByRole = await fetchPractitionerRole(practitionerId, authHeader)
    }
  }
  return startedByRole
}

async function changePasswordHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IPayload
  const retrievalStepInformation = await getRetrievalStepInformation(
    payload.nonce
  ).catch(() => {
    throw unauthorized()
  })

  if (retrievalStepInformation.status !== RetrievalSteps.SECURITY_Q_VERIFIED) {
    return h.response().code(401)
  }

  const remoteAddress =
    request.headers['x-real-ip'] || request.info.remoteAddress
  const userAgent =
    request.headers['x-real-user-agent'] || request.headers['user-agent']

  await changePassword(
    retrievalStepInformation.userId,
    payload.newPassword,
    remoteAddress,
    userAgent
  )
  await deleteRetrievalStepInformation(payload.nonce)
  return h.response().code(200)
}

async function changePassword(
  userId: string,
  password: string,
  remoteAddress: string,
  userAgent: string
) {
  const url = resolve(env.USER_MANAGEMENT_URL, '/changePassword')

  const res = await fetch(url, {
    method: 'POST',
    body: JSON.stringify({ userId, password }),
    headers: {
      'Content-Type': 'application/json',
      'x-real-ip': remoteAddress,
      'x-real-user-agent': userAgent
    }
  })

  if (res.status !== 200) {
    throw Error(res.statusText)
  }
}

async function authenticateHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { code, nonce } = request.payload as IVerifyPayload
  try {
    await checkVerificationCode(nonce, code)
  } catch (err) {
    logger.error(err)
    return unauthorized()
  }
  const { userId, scope } = await getStoredUserInformation(nonce)
  const token = await createToken(
    userId,
    scope,
    WEB_USER_JWT_AUDIENCES,
    JWT_ISSUER
  )
  await deleteUsedVerificationCode(nonce)
  const response: IVerifyResponse = { token }
  const remoteAddress =
    request.headers['x-real-ip'] || request.info.remoteAddress
  const userAgent =
    request.headers['x-real-user-agent'] || request.headers['user-agent']

  try {
    await postUserActionToMetrics(
      'LOGGED_IN',
      response.token,
      remoteAddress,
      userAgent
    )
  } catch (err) {
    logger.error(err)
  }

  return response
}

async function sendUserNameHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IPayload
  const retrievalStepInformation = await getRetrievalStepInformation(
    payload.nonce
  ).catch(() => {
    throw unauthorized()
  })

  if (retrievalStepInformation.status !== RetrievalSteps.SECURITY_Q_VERIFIED) {
    return h.response().code(401)
  }

  const isDemoUser = retrievalStepInformation.scope.indexOf('demo') > -1
  const remoteAddress =
    request.headers['x-real-ip'] || request.info.remoteAddress
  const userAgent =
    request.headers['x-real-user-agent'] || request.headers['user-agent']
  if (!env.isProd || isDemoUser) {
    logger.info(
      `Sending a verification SMS,
        ${JSON.stringify({
          mobile: retrievalStepInformation.mobile,
          username: retrievalStepInformation.username
        })}`
    )
  } else {
    await sendUserName(
      retrievalStepInformation.username,
      retrievalStepInformation.userFullName,
      retrievalStepInformation.mobile,
      retrievalStepInformation.email
    )
  }

  try {
    await postUserActionToMetrics(
      'USERNAME_REMINDER',
      request.headers.authorization,
      remoteAddress,
      userAgent,
      retrievalStepInformation.practitionerId
    )
  } catch (err) {
    logger.error(err.message)
  }

  await deleteRetrievalStepInformation(payload.nonce)
  return h.response().code(200)
}

async function verifyNumberHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
): Promise<IVerifyNumberResponse> {
  const payload = request.payload as IVerifyNumberPayload

  // Looks for step1 data. Throws exception if not found
  const retrievalStepInfo = await getRetrievalStepInformation(
    payload.nonce
  ).catch(() => {
    throw unauthorized()
  })
  // Throws exception if not ready for step2
  if (
    retrievalStepInfo.status !==
    RetrievalSteps.WAITING_FOR_VERIFICATION.toString()
  ) {
    throw unauthorized()
  }
  try {
    // Matchs verification code. Throws exception if doesn't
    await checkVerificationCode(payload.nonce, payload.code)
  } catch (err) {
    logger.error(err)
    throw unauthorized()
  }
  // Update retrievalstep info with new status NUMBER_VERIFIED
  await storeRetrievalStepInformation(
    payload.nonce,
    RetrievalSteps.NUMBER_VERIFIED,
    retrievalStepInfo
  )
  // Returns the securityQuestionKey with nonce
  const response: IVerifyNumberResponse = {
    securityQuestionKey: retrievalStepInfo.securityQuestionKey,
    nonce: payload.nonce
  }
  return response
}

async function refreshHandler(request: Hapi.Request) {
  const { token } = request.payload as IRefreshPayload

  const decodedOrError = verifyToken(token)
  if (decodedOrError._tag === 'Left') {
    return unauthorized()
  }

  const decoded = decodedOrError.right

  const newToken = await refreshToken(decoded)
  return { token: newToken }
}

async function storeVerificationCode(nonce: string, code: string) {
  const codeDetails = {
    code,
    createdAt: Date.now()
  }

  await redis.set(`verification_${nonce}`, JSON.stringify(codeDetails))
}

async function generateVerificationCode(
  nonce: string
): Promise<SixDigitVerificationCode> {
  const code = crypto.randomInt(100000, 999999).toString()
  await storeVerificationCode(nonce, code)
  return code
}

async function getVerificationCodeDetails(
  nonce: string
): Promise<ICodeDetails> {
  const codeDetails = await redis.get(`verification_${nonce}`)

  if (!codeDetails) {
    throw new Error('Auth code not found')
  }

  return JSON.parse(codeDetails) as ICodeDetails
}

function generateNonce() {
  return crypto.randomBytes(16).toString('base64').toString()
}

async function authenticateSuperUserHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IAuthPayload
  let result: IAuthentication
  const { username, password } = payload
  try {
    result = await authenticate(username.trim(), password)
  } catch (err) {
    throw unauthorized()
  }

  if (result.status === 'deactivated') {
    logger.info('Login attempt with a deactivated super user account detected')
    throw unauthorized()
  }

  const SUPER_ADMIN_SCOPES = [
    SCOPES.BYPASSRATELIMIT,
    SCOPES.USER_DATA_SEEDING
  ] satisfies Scope[]

  const token = await createToken(
    result.userId,
    SUPER_ADMIN_SCOPES,
    WEB_USER_JWT_AUDIENCES,
    JWT_ISSUER
  )

  return h.response({
    token
  })
}

async function resendNotificationHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { nonce, retrievalFlow, notificationEvent } =
    request.payload as IResendNotificationPayload

  let userInformation
  try {
    userInformation = retrievalFlow
      ? await getRetrievalStepInformation(nonce)
      : await getStoredUserInformation(nonce)
  } catch (err) {
    return unauthorized()
  }

  const { scope, userFullName, mobile, email } = userInformation

  await generateAndSendVerificationCode(
    nonce,
    scope,
    notificationEvent,
    userFullName,
    mobile,
    email
  )

  return { nonce }
}

async function verifySecurityQuestionHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IPayload

  const retrievalStepInformation = await getRetrievalStepInformation(
    payload.nonce
  ).catch(() => {
    throw unauthorized()
  })

  if (
    retrievalStepInformation.status !==
    RetrievalSteps.NUMBER_VERIFIED.toString()
  ) {
    return unauthorized()
  }
  let verificationResult: IVerifySecurityAnswerResponse
  try {
    verificationResult = await verifySecurityAnswer(
      retrievalStepInformation.userId,
      retrievalStepInformation.securityQuestionKey,
      payload.answer
    )
  } catch (err) {
    return unauthorized()
  }

  // Updates nonce status
  await storeRetrievalStepInformation(
    payload.nonce,
    verificationResult.matched
      ? RetrievalSteps.SECURITY_Q_VERIFIED
      : RetrievalSteps.NUMBER_VERIFIED,
    {
      ...retrievalStepInformation,
      // in case of miss-match, updating the new key otherwise same key
      securityQuestionKey: verificationResult.questionKey
    }
  )

  const response: IResponse = {
    matched: verificationResult.matched,
    nonce: payload.nonce
  }
  if (!verificationResult.matched) {
    response.securityQuestionKey = verificationResult.questionKey
  }
  return response
}

function instanceOfJurisdiction(object: any): object is Location {
  return 'statistics' in object
}

async function fetchLocationsHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const searchParam = request.url.search
  const response = await fetchFromHearth<Saved<Bundle>>(
    `Location${searchParam}`
  )

  response.link = response.link?.map((link) => ({
    ...link,
    url: link.url
      .replace(link.url.split('/Location')[0], `${request.url.origin}`)
      .replace('Location', 'location')
  }))

  response.entry = response.entry?.map((entry) => ({
    ...entry,
    fullUrl: entry.fullUrl
      ?.replace(entry.fullUrl.split('/Location')[0], `${request.url.origin}`)
      .replace('Location', 'location') as URLReference
  }))

  return response
}

async function createLocationHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  if (Array.isArray(request.payload)) {
    return batchLocationsHandler(request.payload as Location[])
  }
  const payload = request.payload as Location | Facility
  const newLocation = composeFhirLocation(payload)
  const partOfLocation = payload.partOf.split('/')[1]

  const locations = [
    ...(await Promise.all([
      getLocationsByIdentifier(
        `${Code.ADMIN_STRUCTURE}_${String(payload.statisticalID)}`
      ),
      getLocationsByIdentifier(
        `${Code.CRVS_OFFICE}_${String(payload.statisticalID)}`
      ),
      getLocationsByIdentifier(
        `${Code.HEALTH_FACILITY}_${String(payload.statisticalID)}`
      )
    ]).then((results) => results.flat()))
  ]

  if (locations.length !== 0) {
    throw conflict(`statisticalID ${payload.statisticalID} already exists`)
  }

  if (partOfLocation !== '0' && Boolean(partOfLocation)) {
    const response = await fetchFromHearth(`Location?_id=${partOfLocation}`)

    if (response.total === 0) {
      throw badRequest(
        `${partOfLocation} is not a valid location for partOfLocation`
      )
    }
  }

  if (instanceOfJurisdiction(payload) && payload.statistics) {
    const statisticalExtensions = generateStatisticalExtensions(
      payload.statistics
    )
    newLocation.extension = statisticalExtensions
  }

  const response = await sendToFhir(
    JSON.stringify(newLocation),
    '/Location',
    'POST',
    request.headers.authorization
  ).catch((err) => {
    throw Error('Cannot create location to FHIR')
  })

  return h.response(response.statusText)
}

async function updateLocationHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const locationId = request.params.locationId
  const location = request.payload as UpdateLocation
  const existingLocation = await fetchFromHearth(`Location?_id=${locationId}`)
  const newLocation = existingLocation.entry[0].resource

  if (existingLocation.total === 0) {
    throw badRequest(`${locationId} is not a valid location`)
  }

  if (location.name) {
    newLocation.name = location.name
  }
  if (location.alias) {
    newLocation.alias = [location.alias]
  }
  if (location.status) {
    newLocation.status = location.status
  }
  if (location.statistics) {
    const statisticalExtensions = updateStatisticalExtensions(
      location.statistics,
      newLocation.extension
    )
    newLocation.extension = statisticalExtensions
  }

  const response = await sendToFhir(
    JSON.stringify(newLocation),
    `/Location/${locationId}`,
    'PUT',
    request.headers.authorization
  ).catch((err) => {
    throw Error('Cannot update location to FHIR')
  })

  return h.response(response.statusText)
}

async function verifySecurityAnswer(
  userId: string,
  questionKey: string,
  answer: string
): Promise<IVerifySecurityAnswerResponse> {
  const url = resolve(env.USER_MANAGEMENT_URL, '/verifySecurityAnswer')

  const res = await fetch(url, {
    method: 'POST',
    body: JSON.stringify({ userId, questionKey, answer }),
    headers: { 'Content-Type': 'application/json' }
  })

  if (res.status !== 200) {
    throw Error(res.statusText)
  }
  const body = await res.json()
  return {
    matched: body.matched,
    questionKey: body.questionKey
  }
}

async function authenticateHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
): Promise<IAuthResponse> {
  const payload = request.payload as IAuthPayload
  let result: IAuthentication

  const { username, password } = payload
  try {
    result = await authenticate(username.trim(), password)
  } catch (err) {
    throw unauthorized()
  }
  if (result.status === 'deactivated') {
    throw forbidden()
  }

  const nonce = generateNonce()
  const response: IAuthResponse = {
    mobile: result.mobile,
    email: result.email,
    status: result.status,
    nonce
  }

  const isPendingUser = response.status && response.status === 'pending'

  const roleScopeMappings = await getUserRoleScopeMapping()

  const role = result.role as keyof typeof roleScopeMappings
  const scopes = roleScopeMappings[role]

  if (isPendingUser) {
    response.token = await createToken(
      result.userId,
      scopes,
      WEB_USER_JWT_AUDIENCES,
      JWT_ISSUER
    )
  } else {
    await storeUserInformation(
      nonce,
      result.name,
      result.userId,
      scopes,
      result.mobile,
      result.email
    )

    const notificationEvent = NotificationEvent.TWO_FACTOR_AUTHENTICATION

    await generateAndSendVerificationCode(
      nonce,
      scopes,
      notificationEvent,
      result.name,
      result.mobile,
      result.email
    )
  }

  return response
}

async function clientCredentialsHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const clientId = request.query.client_id
  const clientSecret = request.query.client_secret

  if (!clientId || !clientSecret) {
    return oauthResponse.invalidRequest(h)
  }

  let result
  try {
    result = await authenticateSystem(clientId, clientSecret)
  } catch (err) {
    return oauthResponse.invalidClient(h)
  }

  if (result.status !== 'active') {
    return oauthResponse.invalidClient(h)
  }

  const isNotificationAPIUser = result.scope.includes(SCOPES.NOTIFICATION_API)

  const token = await createToken(
    result.systemId,
    result.scope,
    isNotificationAPIUser
      ? WEB_USER_JWT_AUDIENCES.concat([NOTIFICATION_API_USER_AUDIENCE])
      : WEB_USER_JWT_AUDIENCES,
    JWT_ISSUER,
    true
  )

  return oauthResponse.success(h, token)
}

async function tokenExchangeHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const subjectToken = request.query.subject_token
  const subjectTokenType = request.query.subject_token_type
  const requestedTokenType = request.query.requested_token_type
  const recordId = request.query.record_id

  if (
    !recordId ||
    !subjectToken ||
    subjectTokenType !== SUBJECT_TOKEN_TYPE ||
    requestedTokenType !== RECORD_TOKEN_TYPE
  ) {
    return oauthResponse.invalidRequest(h)
  }

  const decodedOrError = pipe(subjectToken, verifyToken)
  if (decodedOrError._tag === 'Left') {
    return oauthResponse.invalidSubjectToken(h)
  }
  const { sub } = decodedOrError.right

  // @TODO: If in the future we have a fine grained access control for records, check here that the subject actually has access to the record requested
  const recordToken = await createTokenForRecordValidation(
    sub as UUID,
    recordId
  )

  return oauthResponse.success(h, recordToken)
}

