async function tokenHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const grantType = request.query.grant_type

  if (grantType === 'client_credentials') {
    return clientCredentialsHandler(request, h)
  }

  if (grantType === 'urn:opencrvs:oauth:grant-type:token-exchange') {
    return tokenExchangeHandler(request, h)
  }

  return oauthResponse.invalidGrantType(h)
}

async function invalidateToken(token: string) {
  return redis.setEx(
    `${INVALID_TOKEN_NAMESPACE}:${token}`,
    env.CONFIG_TOKEN_EXPIRY_SECONDS,
    'INVALID'
  )
}

async function getUserRoleScopeMapping() {
  const roles = await fetchJSON<Roles>(
    joinURL(env.COUNTRY_CONFIG_URL_INTERNAL, '/roles')
  )

  logger.info(
    'Country config implements the new /roles response format. Custom scopes apply'
  )

  const defaultRoleMappings = DEFAULT_ROLES_DEFINITION.reduce<
    Record<string, Scope[]>
  >((acc, { id, scopes }) => {
    acc[id] = scopes
    return acc
  }, {})

  const userRoleMappings = roles.reduce<Record<string, Scope[]>>(
    (acc, { id, scopes }) => {
      acc[id] = scopes
      return acc
    },
    {}
  )

  return {
    ...defaultRoleMappings,
    ...userRoleMappings
  }
}

