async function removeJob(myQueue: Queue, id: string) {
  const job = await myQueue.getJob(id)
  if (job) {
    job.remove()
  }
}

function initQueue(): Queue {
  const connection = getRedis()
  logger.info(
    `Initialising queue on REDIS_HOST: ${REDIS_HOST} with connection: ${connection}`
  )
  const newQueue = new Queue(QUEUE_NAME, {
    connection
  })

  EventEmitter.defaultMaxListeners = 50

  const queueEvents = new QueueEvents(QUEUE_NAME, {
    connection
  })

  queueEvents.on('waiting', ({ jobId }: QueueEventType) => {
    logger.info(`A job with ID ${jobId} is waiting`)
  })

  queueEvents.on('active', ({ jobId, prev }: QueueEventType) => {
    logger.info(`Job ${jobId} is now active; previous status was ${prev}`)
  })

  queueEvents.on('completed', ({ jobId, returnvalue }: QueueEventType) => {
    logger.info(`${jobId} has completed and returned ${returnvalue}`)
    removeJob(newQueue, jobId)
  })

  queueEvents.on('failed', ({ jobId, failedReason }: QueueEventType) => {
    logger.info(`${jobId} has failed with reason ${failedReason}`)
  })

  const myWorker: Worker = initWorker(QUEUE_NAME, connection)

  myWorker.on('drained', () => {
    logger.info(`Queue is drained, no more jobs left`)
  })

  myWorker.on('completed', (job: Job) => {
    logger.info(`job ${job.id} has completed`)
  })

  myWorker.on('failed', (job: Job) => {
    logger.info(`job ${job.id} has failed`)
  })
  return newQueue
}

async function subscribeWebhooksHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { hub } = request.payload as ISubscribePayload
  if (!(hub.topic in TRIGGERS)) {
    return h
      .response({
        hub: {
          mode: 'denied',
          topic: hub.topic,
          reason: `Unsupported topic: ${hub.topic}`
        }
      })
      .code(400)
  }
  const token: ITokenPayload = getTokenPayload(
    request.headers.authorization.split(' ')[1]
  )
  const systemId = token.sub

  try {
    const system: ISystem = await getSystem(
      { systemId },
      request.headers.authorization
    )
    if (!system || system.status !== 'active') {
      return h
        .response({
          hub: {
            mode: 'denied',
            topic: hub.topic,
            reason:
              'Active system details cannot be found.  This system is no longer authorized'
          }
        })
        .code(400)
    }

    if (hub.secret !== system.sha_secret) {
      return h
        .response({
          hub: {
            mode: 'denied',
            topic: hub.topic,
            reason: 'hub.secret is incorrect'
          }
        })
        .code(400)
    }

    if (hub.mode !== 'subscribe') {
      return h
        .response({
          hub: {
            mode: 'denied',
            topic: hub.topic,
            reason: 'hub.mode must be set to subscribe'
          }
        })
        .code(400)
    }
    const webhookId = uuid()
    const createdBy = {
      client_id: system.client_id,
      name: system.name,
      type: getScopeType(system.scope),
      username: system.username
    }
    const webhook = {
      webhookId,
      createdBy,
      address: hub.callback,
      sha_secret: hub.secret,
      trigger: hub.topic in TRIGGERS ? hub.topic : undefined
    }
    const challenge = generateChallenge()

    if (!(system.scope.indexOf('demo') > -1)) {
      try {
        const challengeCheck = await fetch(
          resolve(
            hub.callback,
            `?mode=subscribe&challenge=${encodeURIComponent(challenge)}&topic=${
              hub.topic
            }`
          ),
          {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json'
            }
          }
        )
          .then((response) => {
            return response.json()
          })
          .catch((error) => {
            return Promise.reject(
              new Error(` request failed: ${error.message}`)
            )
          })
        if (challenge !== challengeCheck.challenge) {
          throw new Error(
            `${challenge} is not equal to ${challengeCheck.challenge}.  Subscription endpoint check failed`
          )
        }
        await Webhook.create(webhook)
        return h.response().code(202)
      } catch (err) {
        logger.error(err)
        return h.response('Subscription callback check failed').code(400)
      }
    } else {
      try {
        await Webhook.create(webhook)
        return h.response().code(202)
      } catch (err) {
        throw new Error(`Cannot save webhook in development: ${err}`)
      }
    }
  } catch (err) {
    logger.error(err)
    return h.response().code(400)
  }
}

async function listWebhooksHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const token: ITokenPayload = getTokenPayload(
    request.headers.authorization.split(' ')[1]
  )
  const systemId = token.sub
  try {
    const system: ISystem = await getSystem(
      { systemId },
      request.headers.authorization
    )
    if (!system || system.status !== 'active') {
      return h
        .response(
          'Active system details cannot be found.  This system is no longer authorized'
        )
        .code(400)
    }
    try {
      const entries = await Webhook.find({
        'createdBy.client_id': system.client_id
      }).sort({
        createdAt: 'asc'
      })

      const sortedEntries: any = []
      entries.forEach((item) => {
        const entry = {
          id: item.webhookId,
          callback: item.address,
          createdAt: new Date((item.createdAt as number) * 1000).toISOString(),
          createdBy: item.createdBy,
          topic: item.trigger
        }
        sortedEntries.push(entry)
      })
      return h.response({ entries: sortedEntries }).code(200)
    } catch (err) {
      logger.error(err)
      throw internal()
    }
  } catch (err) {
    logger.error(err)
    return h.response().code(400)
  }
}

async function deleteWebhookHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const webhookId = request.params.webhookId
  if (!webhookId) {
    return h.response('No webhook id in URL params').code(400)
  }
  try {
    await Webhook.findOneAndRemove({ webhookId })
  } catch (err) {
    return h.response(`Could not delete webhook: ${webhookId}`).code(400)
  }
  return h.response().code(204)
}

async function deleteWebhookByClientIdHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { clientId } = request.payload as IDeleteWebhooksByClientIdPayload
  if (!clientId) {
    return h.response('No clientId in URL params').code(400)
  }

  try {
    await Webhook.deleteMany({ 'createdBy.client_id': clientId })
  } catch (err) {
    logger.info(
      `Could not delete webhooks associated with client_id: ${clientId}`
    )
  }
  return h.response().code(204)
}

class UpdatePasswordComponent extends React.Component<IFullProps, State> {
  constructor(props: IFullProps) {
    super(props)
    this.state = {
      newPassword: '',
      confirmPassword: '',
      validLength: false,
      hasNumber: false,
      hasCases: false,
      passwordMismatched: false,
      passwordMatched: false,
      continuePressed: false
    }
  }
  validateLength = (value: string) => {
    this.setState(() => ({
      validLength: value.length >= 12
    }))
  }
  validateNumber = (value: string) => {
    this.setState(() => ({
      hasNumber: /\d/.test(value)
    }))
  }
  validateCases = (value: string) => {
    this.setState(() => ({
      hasCases: /[a-z]/.test(value) && /[A-Z]/.test(value)
    }))
  }
  checkPasswordStrength = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value
    this.setState(() => ({
      newPassword: value,
      confirmPassword: '',
      passwordMatched: false,
      passwordMismatched: false,
      continuePressed: false
    }))
    this.validateLength(value)
    this.validateNumber(value)
    this.validateCases(value)
  }
  matchPassword = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newPassword = this.state.newPassword
    const value = event.target.value
    this.setState(() => ({
      confirmPassword: value,
      passwordMismatched: value.length > 0 && newPassword !== value,
      passwordMatched: value.length > 0 && newPassword === value,
      continuePressed: false
    }))
  }
  whatNext = async (event: React.FormEvent) => {
    event.preventDefault()
    this.setState(() => ({
      continuePressed: true,
      passwordMismatched:
        this.state.newPassword.length > 0 &&
        this.state.newPassword !== this.state.confirmPassword
    }))

    if (
      this.state.passwordMatched &&
      this.state.hasCases &&
      this.state.hasNumber &&
      this.state.validLength
    ) {
      // { nonce: string }
      await authApi.changePassword(
        this.props.router.location.state.nonce,
        this.state.newPassword
      )
      this.props.router.navigate(routes.SUCCESS, {
        state: { forgottenItem: FORGOTTEN_ITEMS.PASSWORD }
      })
    }
  }

constructor(props: IFullProps) {
    super(props)
    this.state = {
      newPassword: '',
      confirmPassword: '',
      validLength: false,
      hasNumber: false,
      hasCases: false,
      passwordMismatched: false,
      passwordMatched: false,
      continuePressed: false
    }
  }

