    /**
     * For backwards compatibility, match is added to the router prop.
     */
    const match = { params }
    return (
      <Component
        {...(props as ComponentProps)}
        router={{ location, navigate, params, match }}
      />
    )
  }

function fetch(...params: Parameters<typeof nodeFetch>) {
  return nodeFetch(...params)
}

async function findBirthDuplicateIds(
  authHeader: IAuthHeader,
  birthRegDetails: BirthRegistration
): Promise<{ id: UUID; trackingId: string }

if (!birthRegDetails || !birthRegDetails.child) {
    return []
  }

async function findDuplicateIds(
  registrationDetails: BirthRegistration | DeathRegistration,
  authHeader: IAuthHeader,
  event: EVENT_TYPE
) {
  if (event === EVENT_TYPE.BIRTH) {
    return findBirthDuplicateIds(
      authHeader,
      registrationDetails as BirthRegistration
    )
  } else if (event === EVENT_TYPE.DEATH) {
    return findDeathDuplicateIds(
      authHeader,
      registrationDetails as DeathRegistration
    )
  }
  // NOT IMPLEMENTED FOR MARRIAGE
  return []
}

function updateTaskWithDuplicateIds(
  task: SavedTask,
  duplicateIds: { id: string; trackingId: string }[]
) {
  const extension: Extension = {
    url: FLAGGED_AS_POTENTIAL_DUPLICATE,
    valueString: duplicateIds
      .map((duplicate) => duplicate.trackingId)
      .toString()
  }
  return addExtensionsToTask(
    {
      ...task,
      lastModified: new Date().toISOString()
    },
    [extension]
  ) as SavedTask
}

function updateCompositionWithDuplicateIds(
  composition: Composition,
  duplicateIds: { id: UUID; trackingId: string }[]
) {
  const relatesTo: NonNullable<Composition['relatesTo']> = duplicateIds.map(
    (duplicate) => ({
      code: 'duplicate',
      targetReference: {
        reference: `Composition/${duplicate.id}`
      }
    })
  )
  return addRelatesToToComposition(composition, relatesTo)
}

async function writeJSONToCSV(
  filename: string,
  data: Array<Record<string, any>>
) {
  const csv = stringify(data, {
    header: true
  })
  return fs.promises.writeFile(filename, csv, 'utf8')
}

async function readCSVToJSON<T>(filename: string) {
  return new Promise<T>((resolve, reject) => {
    const chunks: string[] = []
    fs.createReadStream(filename)
      .on('error', reject)
      .pipe(
        csv2json({
          separator: ','
        })
      )
      .on('data', (chunk: string) => chunks.push(chunk))
      .on('error', reject)
      .on('end', () => {
        resolve(JSON.parse(chunks.join('')))
      })
  })
}

function usePersistentCountryBackground() {
  const theme = useTheme()
  const [countryBackground, setCountryBackground] = React.useState({
    backgroundColor: `${(theme as ITheme).colors.purpleDarker}`,
    backgroundImage: '',
    imageFit: ''
  })
  const [offlineBackground, setOfflineBackground] =
    React.useState(countryBackground)
  React.useEffect(() => {
    storage.getItem('country-background').then((res) => {
      res ?? setCountryBackground(JSON.parse(res))
    })
  }, [])

  const background = useSelector(selectCountryBackground)

  if (background && !isEqual(background, offlineBackground)) {
    setOfflineBackground(background)
    storage.setItem('country-background', JSON.stringify(background))
  }

  return offlineBackground
}

