async function unassignRecordHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = validateRequest(
    z.object({
      id: z.string()
    }),
    request.payload
  )

  const token = getToken(request)
  const tokenPayload = getTokenPayload(token)
  const record = await getRecordById(
    // Task history is fetched rather than the task only
    payload.id,
    token,
    [
      'CERTIFIED',
      'VALIDATED',
      'IN_PROGRESS',
      'READY_FOR_REVIEW',
      'REGISTERED',
      'ISSUED',
      'CORRECTION_REQUESTED'
    ],
    true
  )

  const assignment = findAssignment(record)
  if (!assignment) throw new Error('The declaration is not assigned')

  const practitionerId = assignment.practitioner.id
  const lastUser = await getUserOrSystemByCriteria({ practitionerId }, token)

  // Non-registrars can't unassign declarations from registrars
  if (
    !tokenPayload.scope.includes('register') &&
    lastUser.scope?.includes('register')
  ) {
    throw new Error('The declaration cannot be unassigned by this type of user')
  }

  const { unassignedRecord, unassignedRecordWithTaskOnly } = await toUnassigned(
    record,
    token
  )

  await sendBundleToHearth(unassignedRecordWithTaskOnly)
  await indexBundleToRoute(unassignedRecord, token, '/events/unassigned')
  await auditEvent('unassigned', unassignedRecord, token)

  return unassignedRecord
}

async function updateField(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const compositionId = request.params.id
  const { fieldId, valueString, valueBoolean } = request.payload as FieldInput

  const savedRecord = await getValidRecordById(
    compositionId,
    request.headers.authorization,
    false
  )
  const recordResources = savedRecord.entry.map((x) => x.resource)

  // There is only one QuestionnaireResponse in the record
  const questionnaireResponseResource = recordResources.filter((resource) =>
    isQuestionnaireResponse(resource)
  )[0]

  const questionnaireResponseItems = upsertAnswer(
    questionnaireResponseResource.item ?? [],
    { fieldId, valueString, valueBoolean }
  )

  const updatedQuestionnaireResponseResource = {
    resource: {
      ...questionnaireResponseResource,
      item: questionnaireResponseItems
    }
  }

  const updatedRecord = {
    ...savedRecord,
    entry: [updatedQuestionnaireResponseResource]
  }

  await sendBundleToHearth(updatedRecord)
  return h.response(updatedRecord).code(200)
}

