async function getRecordSpecificToken(
  token: string,
  headers: Record<string, string>,
  recordId: string
) {
  const grantType = 'urn:opencrvs:oauth:grant-type:token-exchange'
  const subject_token_type = 'urn:ietf:params:oauth:token-type:access_token'
  const requested_token_type =
    'urn:opencrvs:oauth:token-type:single_record_token'

  const authUrl = new URL(
    `token?grant_type=${grantType}&subject_token=${token}&subject_token_type=${subject_token_type}&requested_token_type=${requested_token_type}&record_id=${recordId}`,
    AUTH_URL
  )

  const res = await fetch(authUrl, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      ...headers
    }
  })

  if (!res.ok) {
    throw new Error(
      `Error calling token exchange handler [${res.statusText} ${
        res.status
      }]: ${await res.text()}`
    )
  }
  return (await res.json()) as { access_token: string }
}

async function getRecordById<T extends Array<keyof StateIdenfitiers>>(
  recordId: string,
  authorizationToken: string,
  allowedStates: T,
  /** Includes TaskHistories in the bundle. Is always required when the bundle is being indexed by ElasticSearch as when searching for records, workqueue views use status histories */
  includeHistoryResources: boolean
): Promise<StateIdenfitiers[T[number]]> {
  const url = new URL(
    `/records/${recordId}${
      includeHistoryResources ? '?includeHistoryResources' : ''
    }`,
    SEARCH_URL
  )
  url.searchParams.append('states', allowedStates.join(','))

  const res = await fetch(url.href, {
    headers: {
      'Content-Type': 'application/json',
      Authorization: authorizationToken
    }
  })

  return res.json()
}

