async function storeVerificationCode(nonce: string, code: string) {
  const codeDetails = {
    code,
    createdAt: Date.now()
  }
  await redis.set(`verification_${nonce}`, JSON.stringify(codeDetails))
}

async function generateAndStoreVerificationCode(
  nonce: string
): Promise<SixDigitVerificationCode> {
  const code = crypto.randomInt(100000, 999999).toString()
  await storeVerificationCode(nonce, code)
  return code
}

async function checkVerificationCode(
  nonce: string,
  code: string
): Promise<void> {
  const codeDetails = await getVerificationCodeDetails(nonce)

  const codeExpired =
    (Date.now() - codeDetails.createdAt) / 1000 >=
    CONFIG_SMS_CODE_EXPIRY_SECONDS

  if (code !== codeDetails.code) {
    throw new Error('sms code invalid')
  }

  if (codeExpired) {
    throw new Error('sms code expired')
  }
}

async function getVerificationCodeDetails(
  nonce: string
): Promise<ICodeDetails> {
  const codeDetails = await redis.get(`verification_${nonce}`)

  if (!codeDetails) {
    throw new Error('sms code not found')
  }

  return JSON.parse(codeDetails) as ICodeDetails
}

async function deleteUsedVerificationCode(
  nonce: string
): Promise<boolean> {
  const count = await redis.del(`verification_${nonce}`)
  return Boolean(count)
}

function generateNonce() {
  return crypto.randomBytes(16).toString('base64').toString()
}

async function createServer() {
  let whitelist: string[] = [HOSTNAME]
  if (HOSTNAME[0] !== '*') {
    whitelist = [LOGIN_URL, CLIENT_APP_URL]
  }
  logger.info(`Whitelist: ${JSON.stringify(whitelist)}`)
  const app = new Hapi.Server({
    host: HOST,
    port: PORT,
    routes: {
      cors: { origin: whitelist },
      validate: {
        failAction: async (_, _2, err: Boom) => {
          if (process.env.NODE_ENV === 'production') {
            // In prod, log a limited error message and throw the default Bad Request error.
            logger.error(`ValidationError: ${err.message}`)
            throw badRequest(`Invalid request payload input`)
          } else {
            // During development, log and respond with the full error.
            logger.error(err.message)
            throw err
          }
        }
      },
      payload: { maxBytes: 52428800, timeout: DEFAULT_TIMEOUT }
    }
  })
  const plugins = getPlugins()

  await app.register(plugins)

  const apolloServer = new ApolloServer<BaseContext>({
    ...getApolloConfig(),
    plugins: [ApolloServerPluginLandingPageLocalDefault({ footer: false })]
  })

  app.auth.strategy('jwt', 'jwt', {
    key: publicCert,
    verifyOptions: {
      algorithms: ['RS256'],
      issuer: 'opencrvs:auth-service',
      audience: 'opencrvs:gateway-user'
    },
    validate: (payload: any, request: Hapi.Request) =>
      validateFunc(payload, request, CHECK_INVALID_TOKEN, AUTH_URL)
  })

  app.auth.default('jwt')

  const routes = getRoutes()
  app.route(routes)

  app.ext('onRequest', (req, h) => {
    if (req.url.pathname.startsWith('/v1')) {
      req.url.pathname = req.url.pathname.replace('/v1', '')
      req.setUrl(req.url)
    }
    return h.continue
  })

  app.ext('onPreResponse', (request, reply) => {
    if (!isBoom(request.response)) {
      request.response.header('access-control-expose-headers', 'X-Version', {
        append: true
      })
      request.response.header(
        'X-Version',
        String(process.env.npm_package_version)
      )
    }

    if (request.response instanceof RateLimitError) {
      return reply
        .response({
          statusCode: 402,
          error: 'Rate limit exceeded',
          message: request.response.message
        })
        .code(402)
    }

    return reply.continue
  })

  /*
   * For debugging sent declarations on pre-prod environments.
   * Custom implementation a sGood doesn't support logging request payloads
   * https://github.com/hapijs/good/search?q=request&type=Issues
   */
  if (process.env.NODE_ENV !== 'production') {
    app.events.on('response', (request) => {
      app.log('info', JSON.stringify(request.payload))
    })
  }

  async function start() {
    await apolloServer.start()

    await app.register({
      plugin: hapiApollo,
      options: {
        path: '/graphql',
        postRoute: {
          options: {
            auth: {
              strategy: 'jwt',
              mode: 'try'
            }
          }
        },
        getRoute: {
          options: {
            auth: {
              strategy: 'jwt',
              mode: 'try'
            }
          }
        },
        context: async ({ request }) => new Context(request),
        apolloServer
      }
    })

    await app.start()
    await database.start()
    app.log('info', `server started on port ${PORT}`)
  }

  async function stop() {
    await app.stop()
    await database.stop()
    app.log('info', 'server stopped')
  }

  return { app, start, stop }
}

