function handleDefaultValue({
  field,
  systemVariables
}: {
  field: FieldConfig
  systemVariables: SystemVariables
}) {
  const defaultValue = field.defaultValue

  if (isFieldConfigDefaultValue(field.defaultValue)) {
    return replacePlaceholders({
      fieldType: field.type,
      defaultValue,
      systemVariables
    })
  }

  return defaultValue
}

function formatDateFieldValue(value: string) {
  return value
    .split('-')
    .map((d: string) => d.padStart(2, '0'))
    .join('-')
}

function makeDatesFormatted<T extends EventState>(
  fields: FieldConfig[],
  values: T
): T {
  return fields.reduce((acc, field) => {
    const fieldId = field.id.replaceAll(DOT_SEPARATOR, FIELD_SEPARATOR)

    if (field.type === FieldType.DATE && fieldId in values) {
      const value = values[fieldId as keyof typeof values]
      if (typeof value === 'string') {
        const formattedDate = formatDateFieldValue(value)
        return { ...acc, [fieldId]: formattedDate }
      }
    }
    return acc
  }, values)
}

function mapFieldsToValues(
  fields: FieldConfig[],
  systemVariables: SystemVariables
) {
  return fields.reduce((memo, field) => {
    const fieldInitialValue = handleDefaultValue({
      field,
      systemVariables
    })

    return { ...memo, [field.id]: fieldInitialValue }
  }, {})
}

function DocumentViewer({
  id,
  options,
  children
}: {
  id?: string
  options: Option<DocumentViewerOptionValue>[]
  children?: React.ReactNode
}) {
  const [selectedOption, setSelectedOption] = useState<
    Option<DocumentViewerOptionValue> | undefined
  >(options[0])

  const onChange = (val: Option<DocumentViewerOptionValue>) =>
    setSelectedOption(val)

  const [zoom, setZoom] = useState(1)
  const [rotation, setRotation] = useState(0)

  const zoomIn = () => {
    setZoom((prev) => prev + 0.2)
  }

  const zoomOut = () => {
    setZoom((prev) => {
      if (prev >= 1) {
        return prev - 0.2
      }
      return prev
    })
  }

  const rotateLeft = () => {
    setRotation((prev) => (prev - 90) % 360)
  }

  return (
    <ViewerWrapper id={id}>
      <ViewerContainer>
        <ViewerHeader>
          <Select
            color="inherit"
            id="select_document"
            options={options}
            value={selectedOption?.value}
            onChange={onChange}
          />
          <PanControls
            rotateLeft={rotateLeft}
            zoomIn={zoomIn}
            zoomOut={zoomOut}
          />
        </ViewerHeader>
        {!!selectedOption && (
          <ViewerImage>
            <PanViewer
              image={selectedOption.value.url}
              rotation={rotation}
              zoom={zoom}
            />
          </ViewerImage>
        )}
        {children}
      </ViewerContainer>
    </ViewerWrapper>
  )
}

