function NumberInput({ value, disabled, ...props }: NumberInputProps) {
  const [inputValue, setInputValue] = React.useState(
    value && isNaN(value) ? undefined : value
  )

  const allowOnlyPositive = props.min !== undefined && props.min >= 0

  return (
    <TextInputComponent
      type={'number'}
      {...props}
      data-testid={`number__${props.id}`}
      isDisabled={disabled}
      value={inputValue}
      onBlur={(e) => {
        props.onChange(inputValue)
        props.onBlur?.(e)
      }}
      onChange={(e) => {
        // Parse the input value as a floating-point number to allow decimal values.
        // If the parsed value is NaN (e.g., when the input is cleared), set inputValue to undefined.
        // Otherwise, update inputValue with the parsed number.
        const updatedValue = parseFloat(e.target.value)
        isNaN(updatedValue)
          ? setInputValue(undefined)
          : setInputValue(updatedValue)
      }}
      onKeyDown={(e: React.KeyboardEvent<HTMLInputElement>) => {
        if (allowOnlyPositive && e.key === '-') {
          e.preventDefault()
        }
      }}
    />
  )
}

function getFieldFromDataEntry({
  intl,
  formData,
  entry
}: {
  intl: IntlShape
  formData: EventState
  entry: { value: TranslationConfig | string; label: TranslationConfig }
}) {
  const { label, value: rawValue } = entry

  // Resolve value if it's a message descriptor
  const formattedValue =
    typeof rawValue === 'object' &&
    'id' in rawValue &&
    'defaultMessage' in rawValue
      ? intl.formatMessage(rawValue)
      : rawValue

  let resolvedValue = formattedValue

  // Match placeholders like {someKey}
  const placeholders = formattedValue.match(/{([^}]+)}/g)
  if (placeholders) {
    placeholders.forEach((placeholder) => {
      const key = placeholder.replace(/{|}/g, '')
      const replacement = formData[key]

      if (replacement == null) {
        throw new Error(`Could not resolve placeholder: ${placeholder}`)
      }

      resolvedValue = resolvedValue.replace(placeholder, replacement.toString())
    })
  }

  return {
    value: resolvedValue,
    config: {
      type: FieldType.TEXT,
      id: label.id,
      label
    }
  }
}

function DataInput({
  configuration,
  label,
  formData,
  declarationFields
}: FieldProps<'DATA'> & {
  formData: EventState
  declarationFields: Inferred[]
}) {
  const intl = useIntl()
  const { subtitle, data } = configuration
  const title = label.defaultMessage ? intl.formatMessage(label) : ''

  const fields = data.map((entry) => {
    if ('fieldId' in entry) {
      return {
        value: formData[entry.fieldId],
        config: declarationFields.find((f) => f.id === entry.fieldId)
      }
    }

    return getFieldFromDataEntry({
      intl,
      formData,
      entry
    })
  })

  return (
    <Container>
      {title && <label>{title}</label>}
      {subtitle && <Subtitle>{intl.formatMessage(subtitle)}</Subtitle>}
      <dl>
        {fields
          // We don't want to display fields that are conditionally hidden in the original form configuration
          .filter(({ config }) => config && isFieldVisible(config, formData))
          .map(({ config, value }) => {
            if (!config) {
              return null
            }

            return (
              <React.Fragment key={config.id}>
                <dt>{intl.formatMessage(config.label)}</dt>
                <dd>
                  <Output
                    field={config}
                    showPreviouslyMissingValuesAsChanged={false}
                    value={value}
                  />
                </dd>
              </React.Fragment>
            )
          })}
      </dl>
    </Container>
  )
}

function SelectInput({
  onChange,
  value,
  ...props
}: Omit<FieldProps<'SELECT'>, 'label'> & {
  onChange: (newValue: string) => void
  value?: string
  label?: TranslationConfig
} & { 'data-testid'?: string }) {
  const intl = useIntl()
  const { options } = props
  const selectedOption = options.find((option) => option.value === value)
  const formattedOptions = options.map((option: SelectOption) => ({
    value: option.value,
    label: intl.formatMessage(option.label)
  }))

  const inputValue = selectedOption?.value ?? ''

  return (
    <SelectComponent
      {...props}
      data-testid={props['data-testid'] || `select__${props.id}`}
      options={formattedOptions}
