function EventSummary({
  event,
  eventConfiguration
}: {
  event: Record<string, FieldValue | null>
  eventConfiguration: EventConfig
}) {
  const intl = useIntlFormatMessageWithFlattenedParams()
  const { summary, label } = eventConfiguration
  const defaultFields = getDefaultFields(label)
  const summaryPageFields = [...defaultFields, ...summary.fields]
  const declarationFields = getDeclarationFields(eventConfiguration)

  const fields = summaryPageFields.map((field) => {
    if (field.conditionals && !areConditionsMet(field.conditionals, event)) {
      return null
    }

    if ('fieldId' in field) {
      const config = declarationFields.find((f) => f.id === field.fieldId)
      const value = event[field.fieldId] ?? undefined

      if (!config) {
        return null
      }

      return {
        id: field.fieldId,
        // If a custom label is configured, use it. Otherwise, by default, use the label from the original form field.
        label: field.label ?? config.label,
        emptyValueMessage: field.emptyValueMessage,
        value: Output({
          field: config,
          showPreviouslyMissingValuesAsChanged: false,
          value
        })
      }
    }

    return {
      id: field.id,
      label: field.label,
      emptyValueMessage: field.emptyValueMessage,
      value: intl.formatMessage(field.value, event)
    }
  })

  return (
    <>
      <Summary id="summary">
        {fields
          .filter((f): f is NonNullable<typeof f> => f !== null)
          .map((field) => (
            <Summary.Row
              key={field.id}
              data-testid={field.id}
              label={intl.formatMessage(field.label)}
              placeholder={
                field.emptyValueMessage &&
                intl.formatMessage(field.emptyValueMessage)
              }
              value={field.value}
            />
          ))}
      </Summary>
    </>
  )
}

function getFileNames(actions: ActionDocument[]): string[] {
  return actions.flatMap((action) =>
    Object.values(action.declaration).flatMap((value) => {
      // Handle single file field
      const fileParsed = FileFieldValue.safeParse(value)
      if (fileParsed.success) {
        return [fileParsed.data.filename]
      }

      // Handle multiple file field (file with options)
      const fileOptionParsed = FileFieldWithOptionValue.safeParse(value)
      if (fileOptionParsed.success) {
        return fileOptionParsed.data.map((val) => val.filename)
      }

      return []
    })
  )
}

async function cacheFiles(eventDocument: EventDocument) {
  const actions = getAcceptedActions(eventDocument)
  const fileNames = getFileNames(actions)

  return Promise.all(fileNames.map(async (filename) => precacheFile(filename)))
}

async function removeCachedFiles(eventDocument: EventDocument) {
  const actions = getAcceptedActions(eventDocument)
  const fileNames = getFileNames(actions)

  return Promise.all(fileNames.map(removeCached))
}

