function isValidPlainDate(rawDate: unknown): rawDate is PlainDate {
  if (typeof rawDate !== 'string') {
    return false
  }
  const [yyyy, mm, dd] = rawDate.split('-')
  if (
    !dd ||
    !mm ||
    !yyyy ||
    dd.length === 0 ||
    mm.length === 0 ||
    yyyy.length < 4 ||
    !isValid(new Date(rawDate))
  ) {
    return false
  }
  return true
}

function plainDateToLocalDate(plainDate: PlainDate) {
  const rawDate = plainDate as unknown as `${number}-${number}-${number}`
  const [yyyy, mm, dd] = rawDate.split('-')
  return new Date(Number(yyyy), Number(mm) - 1, Number(dd))
}

function formatPlainDate(
  plainDate: PlainDate,
  formatString = 'dd MMMM yyyy'
) {
  const localDate = plainDateToLocalDate(plainDate)
  return format(localDate, formatString, {
    locale: locales[window.__localeId__]
  })
}

function formatDate(
  date: Date | number,
  formatStr = 'PP',
  locale?: string
) {
  if (!isValid(date)) {
    return ''
  }
  return format(date, formatStr, {
    locale: locales[locale ?? window.__localeId__]
  })
}

