function DateRangePickerForFormFieldComponent(props: IDateRangePickerProps) {
  const { intl } = props
  const [modalVisible, setModalVisible] = useState<boolean>(false)

  const handleLinkOnClick = () => {
    setModalVisible(true)
  }

  const handleExactDateChange = (val: string) => {
    props.onChange({ ...props.value, exact: val })
  }

  const handleDateRangeChange = ({ startDate, endDate }: IDateRange) => {
    props.onChange({
      ...props.value,
      rangeStart: startDate?.toISOString(),
      rangeEnd: endDate?.toISOString(),
      isDateRangeActive: true
    })
  }

  const handleDateRangeActiveChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    props.onChange({
      ...props.value,
      isDateRangeActive: event.target.checked
    })
  }

  const formatDateRangeLabel = (
    rangeStart: string | undefined,
    rangeEnd: string | undefined
  ) => {
    if (!rangeStart || !rangeEnd) {
      return
    }
    const dateStartLocale =
      rangeStart && format(new Date(rangeStart), 'MMMM yyyy')
    const dateEndLocale = rangeEnd && format(new Date(rangeEnd), 'MMMM yyyy')

    return intl.formatMessage(messages.dateRangePickerCheckboxLabel, {
      rangeStart: dateStartLocale,
      rangeEnd: dateEndLocale
    })
  }

async function updateDateField(
    wrapper: ReactWrapper,
    id: string,
    dateValue: Date
  ) {
    const dayInput = wrapper.find(`input#${id}-dd`).hostNodes()
    const monthInput = wrapper.find(`input#${id}-mm`).hostNodes()
    const yearInput = wrapper.find(`input#${id}-yyyy`).hostNodes()

    dayInput.simulate('focus')
    dayInput.simulate('change', {
      target: { id: `${id}-dd`, value: dateValue.getDay().toString() }
    })
    dayInput.simulate('blur')

    monthInput.simulate('focus')
    monthInput.simulate('change', {
      target: { id: `${id}-mm`, value: (dateValue.getMonth() + 1).toString() }
    })
    monthInput.simulate('blur')

    yearInput.simulate('focus')
    yearInput.simulate('change', {
      target: { id: `${id}-yyyy`, value: dateValue.getFullYear().toString() }
    })
    yearInput.simulate('blur')

    await flushPromises()
    component.update()
  }

componentDidUpdate(prevProps: IFullProps) {
    const hash = this.props.router.location?.hash
    const hashChanged = hash && hash !== prevProps.router.location.hash
    const appName = this.props.offlineData
      ? this.props.offlineData.config.APPLICATION_NAME
      : ''
    if (appName) document.title = appName
    if (hashChanged) {
      // Push onto callback queue so it runs after the DOM is updated,
      // this is required when navigating from a different page so that
      // the element is rendered on the page before trying to getElementById.
      setTimeout(() => {
        const id = hash.replace('#', '')
        const element = document.getElementById(id)
        if (element) {
          element.scrollIntoView()
        }
      }, 0)
    }
    if (this.props.loadingError && isNavigatorOnline()) {
      this.props.showConfigurationErrorNotification()
    }
    if (prevProps.loadingError && !this.props.loadingError) {
      this.props.hideConfigurationErrorNotification()
    }
  }

if (!assignment) {
      const assign = await openModal<boolean>((close) => (
        <AssignModal close={close} />
      ))
      if (assign) {
        download()
      }
    }

stopPropagation()
  }

  if (status && LOADING_STATUSES.includes(status)) {
    return (
      <StatusIndicator
        isLoading={true}
        className={className}
        id={`${id}-download-loading`}
      >
        <Spinner id={`action-loading-${id}`} size={24} />
      </StatusIndicator>
    )
  }

