                key={index}
                disabled={
                  (minDate && isBefore(monthDate, minDate)) ||
                  isAfter(monthDate, maxDate)
                }
                selected={
                  isSameMonth(selectedDate, monthDate) &&
                  isSameYear(selectedDate, monthDate)
