  }

  const selectedPresetFromProps = presetOptions.find(
    (item) =>
      !isPresetNavButton(item) &&
      isSameMonth(startDateFromProps, item.startDate) &&
      isSameMonth(endDateFromProps, item.endDate)
  )

  const routes: ROUTES = {
    [PRESET]: {
      renderComponent: () => (
        <PresetSelector
