function RequiresUpdateComponent(props: IRejectTabProps) {
  const navigate = useNavigate()

  const { width } = useWindowSize()
  const [sortedCol, setSortedCol] = useState<COLUMNS>(COLUMNS.SENT_FOR_UPDATES)
  const [sortOrder, setSortOrder] = useState<SORT_ORDER>(SORT_ORDER.ASCENDING)

  const onColumnClick = (columnName: string) => {
    const { newSortedCol, newSortOrder } = changeSortedColumn(
      columnName,
      sortedCol,
      sortOrder
    )
    setSortedCol(newSortedCol)
    setSortOrder(newSortOrder)
  }

  const getColumns = () => {
    if (width > props.theme.grid.breakpoints.lg) {
      return [
        {
          width: 30,
          label: props.intl.formatMessage(constantsMessages.name),
          key: COLUMNS.ICON_WITH_NAME,
          isSorted: sortedCol === COLUMNS.NAME,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.event),
          width: 16,
          key: COLUMNS.EVENT,
          isSorted: sortedCol === COLUMNS.EVENT,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.eventDate),
          width: 18,
          key: COLUMNS.DATE_OF_EVENT,
          isSorted: sortedCol === COLUMNS.DATE_OF_EVENT,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.sentForUpdates),
          width: 18,
          key: COLUMNS.SENT_FOR_UPDATES,
          isSorted: sortedCol === COLUMNS.SENT_FOR_UPDATES,
          sortFunction: onColumnClick
        },
        {
          width: 18,
          alignment: ColumnContentAlignment.RIGHT,
          key: COLUMNS.ACTIONS,
          isActionColumn: true
        }
      ]
    } else {
      return [
        {
          label: props.intl.formatMessage(constantsMessages.name),
          width: 70,
          key: COLUMNS.ICON_WITH_NAME_EVENT
        },
        {
          width: 30,
          alignment: ColumnContentAlignment.RIGHT,
          key: COLUMNS.ACTIONS,
          isActionColumn: true
        }
      ]
    }
  }

  const transformRejectedContent = (data: GQLEventSearchResultSet) => {
    const { intl } = props
    if (!data || !data.results) {
      return []
    }

    const validateScopes = [
      SCOPES.RECORD_REGISTER,
      SCOPES.RECORD_SUBMIT_FOR_APPROVAL,
      SCOPES.RECORD_SUBMIT_FOR_UPDATES
    ] as Scope[]

    const isReviewer = props.scope?.some((x) => validateScopes.includes(x))

    const transformedData = transformData(data, props.intl)
    const items = transformedData.map((reg, index) => {
      const actions = [] as IAction[]
      const foundDeclaration = props.outboxDeclarations.find(
        (declaration) => declaration.id === reg.id
      )
      const downloadStatus = foundDeclaration?.downloadStatus
      const isDuplicate = reg.duplicates && reg.duplicates.length > 0

      if (downloadStatus !== DOWNLOAD_STATUS.DOWNLOADED) {
        if (width > props.theme.grid.breakpoints.lg && isReviewer) {
          actions.push({
            label: props.intl.formatMessage(buttonMessages.update),
            handler: () => {},
            disabled: true
          })
        }
      } else {
        if (width > props.theme.grid.breakpoints.lg && isReviewer) {
          actions.push({
            label: props.intl.formatMessage(buttonMessages.update),
            handler: (
              e: React.MouseEvent<HTMLButtonElement, MouseEvent> | undefined
            ) => {
              if (e) {
                e.stopPropagation()
              }

              navigate(
                generateGoToPageUrl({
                  pageRoute: REVIEW_EVENT_PARENT_FORM_PAGE,
                  declarationId: reg.id,
                  pageId: 'review',
                  event: reg.event ? reg.event.toLowerCase() : ''
                })
              )
            }
          })
        }
      }
      actions.push({
        actionComponent: (
          <DownloadButton
            downloadConfigs={{
              event: reg.event,
              compositionId: reg.id,
              action: DownloadAction.LOAD_REVIEW_DECLARATION,
              assignment:
                foundDeclaration?.assignmentStatus ??
                reg.assignment ??
                undefined
            }}
            key={`DownloadButton-${index}`}
            status={downloadStatus as DOWNLOAD_STATUS}
            declarationStatus={reg.declarationStatus as SUBMISSION_STATUS}
          />
        )
      })
      const event =
        (reg.event &&
          intl.formatMessage(
            dynamicConstantsMessages[reg.event.toLowerCase()]
          )) ||
        ''
      const sentForUpdates =
        getPreviousOperationDateByOperationType(
          reg.operationHistories,
          RegStatus.Rejected
        ) || ''

      const dateOfEvent =
        reg.dateOfEvent &&
        reg.dateOfEvent.length > 0 &&
        plainDateToLocalDate(reg.dateOfEvent)
      const NameComponent = reg.name ? (
        <NameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab: 'rejectTab',
                declarationId: reg.id
              })
            )
          }
        >
          {reg.name}
        </NameContainer>
      ) : (
        <NoNameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab: 'rejectTab',
                declarationId: reg.id
              })
            )
          }
        >
          {intl.formatMessage(constantsMessages.noNameProvided)}
        </NoNameContainer>
      )
      return {
        ...reg,
        event,
        name: reg.name && reg.name.toLowerCase(),
        iconWithName: (
          <IconWithName
            status={reg.declarationStatus}
            name={NameComponent}
            isDuplicate={isDuplicate}
          />
        ),
        iconWithNameEvent: (
          <IconWithNameEvent
            status={reg.declarationStatus}
            name={NameComponent}
            event={reg.event}
            isDuplicate={isDuplicate}
          />
        ),
        sentForUpdates,
        dateOfEvent,
        actions
      }
    })
    const sortedItems = getSortedItems(items, sortedCol, sortOrder)
    return sortedItems.map((item) => {
      return {
        ...item,
        dateOfEvent:
          item.dateOfEvent && formattedDuration(item.dateOfEvent as Date),
        sentForUpdates:
          item.sentForUpdates && formattedDuration(item.sentForUpdates as Date)
      }
    })
  }

  const totalPages = props.queryData.data.totalItems
    ? Math.ceil(props.queryData.data.totalItems / props.pageSize)
    : 0
  const isShowPagination =
    props.queryData.data.totalItems &&
    props.queryData.data.totalItems > props.pageSize
      ? true
      : false
  return (
    <WQContentWrapper
      title={props.intl.formatMessage(navigationMessages.requiresUpdate)}
      isMobileSize={width < props.theme.grid.breakpoints.lg}
      isShowPagination={isShowPagination}
      paginationId={props.paginationId}
      totalPages={totalPages}
      onPageChange={props.onPageChange}
      loading={props.loading}
      error={props.error}
      noResultText={props.intl.formatMessage(
        wqMessages.noRecordsRequireUpdates
      )}
      noContent={transformRejectedContent(props.queryData.data).length <= 0}
    >
      <Workqueue
        content={transformRejectedContent(props.queryData.data)}
        columns={getColumns()}
        loading={props.loading}
        sortOrder={sortOrder}
        hideLastBorder={!isShowPagination}
      />
    </WQContentWrapper>
  )
}

function mapStateToProps(state: IStoreState) {
  return {
    scope: getScope(state),
    outboxDeclarations: state.declarationsState.declarations
  }
}

function InExternalValidationComponent(props: IProps) {
  const navigate = useNavigate()
  const [sortedCol, setSortedCOl] = React.useState<COLUMNS>(COLUMNS.NAME)
  const [sortOrder, setSortOrder] = React.useState<SORT_ORDER>(
    SORT_ORDER.ASCENDING
  )
  const { width: viewportWidth } = useWindowSize()
  const { intl, queryData, paginationId, pageSize, onPageChange } = props
  const { data } = queryData
  const totalPages = props.queryData.data.totalItems
    ? Math.ceil(props.queryData.data.totalItems / pageSize)
    : 0
  const isShowPagination =
    props.queryData.data.totalItems &&
    props.queryData.data.totalItems > pageSize
      ? true
      : false

  const onColumnClick = (columnName: string) => {
    const { newSortedCol, newSortOrder } = changeSortedColumn(
      columnName,
      sortedCol,
      sortOrder
    )
    setSortedCOl(newSortedCol)
    setSortOrder(newSortOrder)
  }

  const transformWaitingValidationContent = (data: GQLEventSearchResultSet) => {
    const { intl } = props
    if (!data || !data.results) {
      return []
    }
    const transformedData = transformData(data, props.intl)

    const items = transformedData.map((reg, index) => {
      const event =
        (reg.event &&
          intl.formatMessage(
            dynamicConstantsMessages[reg.event.toLowerCase()]
          )) ||
        ''
      const dateOfEvent =
        reg.dateOfEvent &&
        reg.dateOfEvent.length > 0 &&
        plainDateToLocalDate(reg.dateOfEvent)
      const sentForValidation =
        (reg.modifiedAt && Number.isNaN(Number(reg.modifiedAt))
          ? new Date(reg.modifiedAt)
          : new Date(Number(reg.modifiedAt))) || ''
      const NameComponent = reg.name ? (
        <NameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab: 'externalValidationTab',
                declarationId: reg.id
              })
            )
          }
        >
          {reg.name}
        </NameContainer>
      ) : (
        <NoNameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab: 'externalValidationTab',
                declarationId: reg.id
              })
            )
          }
        >
          {intl.formatMessage(constantsMessages.noNameProvided)}
        </NoNameContainer>
      )
      return {
        ...reg,
        event,
        name: reg.name && reg.name.toLowerCase(),
        iconWithName: (
          <IconWithName status={reg.declarationStatus} name={NameComponent} />
        ),
        iconWithNameEvent: (
          <IconWithNameEvent
            status={reg.declarationStatus}
            name={NameComponent}
            event={event}
          />
        ),
        actions: [],
        dateOfEvent,
        sentForValidation,
        rowClickHandler: [
          {
            label: 'rowClickHandler',
            handler: () =>
              navigate(
                formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                  tab: 'externalValidationTab',
                  declarationId: reg.id
                })
              )
          }
        ]
      }
    })
    const sortedItems = getSortedItems(items, sortedCol, sortOrder)
    return sortedItems.map((item) => {
      return {
        ...item,
        dateOfEvent:
          item.dateOfEvent && formattedDuration(item.dateOfEvent as Date),
        sentForValidation:
          item.sentForValidation &&
          formattedDuration(item.sentForValidation as Date)
      }
    })
  }

  const columns =
    viewportWidth > props.theme.grid.breakpoints.lg
      ? [
          {
            width: 30,
            label: props.intl.formatMessage(constantsMessages.name),
            key: COLUMNS.ICON_WITH_NAME,
            isSorted: sortedCol === COLUMNS.NAME,
            sortFunction: onColumnClick
          },
          {
            label: props.intl.formatMessage(constantsMessages.event),
            width: 16,
            key: COLUMNS.EVENT,
            isSorted: sortedCol === COLUMNS.EVENT,
            sortFunction: onColumnClick
          },
          {
            label: props.intl.formatMessage(constantsMessages.eventDate),
            width: 18,
            key: COLUMNS.DATE_OF_EVENT,
            isSorted: sortedCol === COLUMNS.DATE_OF_EVENT,
            sortFunction: onColumnClick
          },
          {
            label: props.intl.formatMessage(constantsMessages.sentForReview),
            width: 18,
            key: COLUMNS.SENT_FOR_VALIDATION,
            isSorted: sortedCol === COLUMNS.SENT_FOR_VALIDATION,
            sortFunction: onColumnClick
          }
        ]
      : [
          {
            label: props.intl.formatMessage(constantsMessages.name),
            width: 90,
            key: COLUMNS.ICON_WITH_NAME_EVENT
          }
        ]

  return (
    <WQContentWrapper
      title={intl.formatMessage(navigationMessages.waitingValidation)}
      isMobileSize={viewportWidth < props.theme.grid.breakpoints.lg}
      isShowPagination={isShowPagination}
      paginationId={paginationId}
      totalPages={totalPages}
      onPageChange={onPageChange}
      noResultText={intl.formatMessage(wqMessages.noRecordsExternalValidation)}
      loading={props.loading}
      error={props.error}
      noContent={transformWaitingValidationContent(data).length <= 0}
    >
      <Workqueue
        content={transformWaitingValidationContent(data)}
        loading={props.loading}
        columns={columns}
        sortOrder={sortOrder}
        hideLastBorder={!isShowPagination}
      />
    </WQContentWrapper>
  )
}

function setDateRangePickerValues(startDate: Date, endDate: Date) {
    setState((prevState) => ({
      ...prevState,
      timeStart: startDate,
      timeEnd: endDate
    }))
  }

function toggleSortOrder(columnName: SORTED_COLUMN) {
    setState((prevState) => ({
      ...prevState,
      sortedColumn: columnName,
      sortOrder:
        prevState.sortOrder === SORT_ORDER.DESCENDING
          ? SORT_ORDER.ASCENDING
          : SORT_ORDER.DESCENDING
    }))
  }

function InProgressComponent(props: IRegistrarHomeProps) {
  const navigate = useNavigate()
  const { width } = useWindowSize()

  const [sortedCol, setSortedCol] = useState<COLUMNS>(COLUMNS.NOTIFICATION_SENT)
  const [sortOrder, setSortOrder] = useState<SORT_ORDER>(SORT_ORDER.DESCENDING)

  const storedDeclarations = useSelector(
    (state: IStoreState) => state.declarationsState.declarations
  )

  const onColumnClick = (columnName: string) => {
    const { newSortedCol, newSortOrder } = changeSortedColumn(
      columnName,
      sortedCol,
      sortOrder
    )
    setSortOrder(newSortOrder)
    setSortedCol(newSortedCol)
  }

  const transformRemoteDraftsContent = (data: GQLEventSearchResultSet) => {
    if (!data || !data.results) {
      return []
    }

    const { intl } = props
    const { locale } = intl

    const items = data.results.map((reg, index) => {
      if (!reg) {
        throw new Error('Registration is null')
      }

      const storedDeclaration = storedDeclarations.find((d) => d.id === reg.id)

      const regId = reg.id
      const event = reg.type
      const lastModificationDate =
        (reg.operationHistories &&
          getPreviousOperationDateByOperationType(
            reg.operationHistories as ITaskHistory[],
            RegStatus.InProgress
          )) ||
        ''
      const pageRoute = REVIEW_EVENT_PARENT_FORM_PAGE

      let name
      let eventDate = ''

      if (reg.registration) {
        if (isBirthEvent(reg)) {
          const names = reg.childName as GQLHumanName[]
          const namesMap = createNamesMap(names)
          name = namesMap[locale] || namesMap[LANG_EN]
          eventDate = reg.dateOfBirth
        } else if (isDeathEvent(reg)) {
          const names = reg.deceasedName as GQLHumanName[]
          const namesMap = createNamesMap(names)
          name = namesMap[locale] || namesMap[LANG_EN]
          const date = reg.dateOfDeath
          eventDate = date && date
        } else if (isMarriageEvent(reg)) {
          const groomNames = reg.groomName as GQLHumanName[]
          const groomNamesMap = createNamesMap(groomNames)
          const brideNames = reg.brideName as GQLHumanName[]
          const brideNamesMap = createNamesMap(brideNames)
          const groomName = groomNamesMap[locale] || groomNamesMap[LANG_EN]
          const brideName = brideNamesMap[locale] || brideNamesMap[LANG_EN]
          name =
            brideName && groomName
              ? `${groomName} & ${brideName}`
              : brideName || groomName || EMPTY_STRING
          const date = reg.dateOfMarriage
          eventDate = date && date
        }
      }

      const dateOfEvent = isValidPlainDate(eventDate)
        ? plainDateToLocalDate(eventDate)
        : ''
      const actions: IAction[] = []
      const foundDeclaration = props.outboxDeclarations.find(
        (declaration) => declaration.id === reg.id
      )
      const downloadStatus = foundDeclaration?.downloadStatus

      if (width > props.theme.grid.breakpoints.lg) {
        actions.push({
          label: intl.formatMessage(buttonMessages.update),
          handler: () => {
            if (downloadStatus === DOWNLOAD_STATUS.DOWNLOADED) {
              navigate(
                generateGoToPageUrl({
                  pageRoute,
                  declarationId: regId,
                  pageId: 'review',
                  event: (event && event.toLowerCase()) || ''
                })
              )
            }
          },
          disabled: downloadStatus !== DOWNLOAD_STATUS.DOWNLOADED
        })
      }
      if (reg.registration?.status) {
        actions.push({
          actionComponent: (
            <DownloadButton
              downloadConfigs={{
                event: event as string,
                compositionId: reg.id,
                action: DownloadAction.LOAD_REVIEW_DECLARATION,
                assignment:
                  storedDeclaration?.assignmentStatus ??
                  reg?.registration?.assignment
              }}
              key={`DownloadButton-${index}`}
              status={downloadStatus}
              declarationStatus={reg.registration.status as SUBMISSION_STATUS}
            />
          )
        })
      }

      const NameComponent = name ? (
        <NameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab:
                  props.selectorId === SELECTOR_ID.hospitalDrafts
                    ? 'notificationTab'
                    : 'inProgressTab',
                declarationId: regId
              })
            )
          }
        >
          {name}
        </NameContainer>
      ) : (
        <NoNameContainer
          id={`name_${index}`}
          onClick={() =>
            navigate(
              formatUrl(routes.DECLARATION_RECORD_AUDIT, {
                tab: 'inProgressTab',
                declarationId: regId
              })
            )
          }
        >
          {intl.formatMessage(constantsMessages.noNameProvided)}
        </NoNameContainer>
      )

      window.__localeId__ = locale
      return {
        id: regId,
        event:
          (event &&
            intl.formatMessage(
              dynamicConstantsMessages[event.toLowerCase()]
            )) ||
          '',
        name: name && name.toString().toLowerCase(),
        iconWithName: (
          <IconWithName
            status={reg.registration?.status || SUBMISSION_STATUS.DRAFT}
            name={NameComponent}
            isDuplicate={(reg.registration?.duplicates?.length ?? 0) > 0}
          />
        ),
        iconWithNameEvent: (
          <IconWithNameEvent
            status={reg.registration?.status || SUBMISSION_STATUS.DRAFT}
            name={NameComponent}
            event={event}
            isDuplicate={(reg.registration?.duplicates?.length ?? 0) > 0}
          />
        ),
        dateOfEvent,
        notificationSent: lastModificationDate || '',
        actions
      }
    })
    const sortedItems = getSortedItems(items, sortedCol, sortOrder)
    return sortedItems.map((item) => {
      return {
        ...item,
        notificationSent:
          item.notificationSent &&
          /* eslint-disable-next-line @typescript-eslint/no-explicit-any */
          formattedDuration(item.notificationSent as any),
        dateOfEvent:
          item.dateOfEvent && formattedDuration(item.dateOfEvent as Date)
      }
    })
  }

  const getColumns = () => {
    if (width > props.theme.grid.breakpoints.lg) {
      return [
        {
          label: props.intl.formatMessage(constantsMessages.name),
          width: 30,
          key: COLUMNS.ICON_WITH_NAME,
          isSorted: sortedCol === COLUMNS.NAME,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.event),
          width: 16,
          key: COLUMNS.EVENT,
          isSorted: sortedCol === COLUMNS.EVENT,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.eventDate),
          width: 18,
          key: COLUMNS.DATE_OF_EVENT,
          isSorted: sortedCol === COLUMNS.DATE_OF_EVENT,
          sortFunction: onColumnClick
        },
        {
          label: props.intl.formatMessage(constantsMessages.notificationSent),
          width: 18,
          key: COLUMNS.NOTIFICATION_SENT,
          isSorted: sortedCol === COLUMNS.NOTIFICATION_SENT,
          sortFunction: onColumnClick
        },
        {
          width: 18,
          key: COLUMNS.ACTIONS,
          isActionColumn: true,
          alignment: ColumnContentAlignment.RIGHT
        }
      ]
    } else {
      return [
        {
          label: props.intl.formatMessage(constantsMessages.name),
          width: 70,
          key: COLUMNS.ICON_WITH_NAME_EVENT
        },
        {
          width: 30,
          key: COLUMNS.ACTIONS,
          isActionColumn: true,
          alignment: ColumnContentAlignment.RIGHT
        }
      ]
    }
  }

  const getTabs = (
    selectorId: string,
    fieldAgentCount: number,
    hospitalCount: number
  ) => {
    const tabs = {
      activeTabId: selectorId || SELECTOR_ID.fieldAgentDrafts,
      onTabClick: (selectorId: string) => {
        navigate(
          generateGoToHomeTabUrl({
            tabId: WORKQUEUE_TABS.inProgress,
            selectorId
          })
        )
      },
      sections: [
        {
          id: SELECTOR_ID.fieldAgentDrafts,
          title: `${props.intl.formatMessage(
            messages.inProgressFieldAgents
          )} (${fieldAgentCount})`,
          disabled: false
        },
        {
          id: SELECTOR_ID.hospitalDrafts,
          title: `${props.intl.formatMessage(
            messages.hospitalDrafts
          )} (${hospitalCount})`,
          disabled: false
        }
      ]
    }

    return (
      <FormTabs
        sections={tabs.sections}
        activeTabId={tabs.activeTabId}
        onTabClick={(id: string) => tabs.onTabClick(id)}
      />
    )
  }

  const renderFieldAgentTable = (
    data: GQLEventSearchResultSet,
    isShowPagination: boolean
  ) => {
    return (
      <Workqueue
        content={transformRemoteDraftsContent(data)}
        columns={getColumns()}
        loading={props.loading}
        sortOrder={sortOrder}
        hideLastBorder={!isShowPagination}
      />
    )
  }

  const renderHospitalTable = (
    data: GQLEventSearchResultSet,
    isShowPagination: boolean
  ) => {
    return (
      <Workqueue
        content={transformRemoteDraftsContent(data)}
        columns={getColumns()}
        loading={props.loading}
        sortOrder={sortOrder}
        hideLastBorder={!isShowPagination}
      />
    )
  }

  const { intl, selectorId, queryData, onPageChange } = props

  const isShowPagination =
    !props.selectorId || props.selectorId === SELECTOR_ID.fieldAgentDrafts
      ? props.queryData.inProgressData &&
        props.queryData.inProgressData.totalItems &&
        props.queryData.inProgressData.totalItems > props.pageSize
        ? true
        : false
      : props.queryData.notificationData &&
          props.queryData.notificationData.totalItems &&
          props.queryData.notificationData.totalItems > props.pageSize
        ? true
        : false

  const { inProgressData, notificationData } = queryData
  const paginationId =
    selectorId === SELECTOR_ID.fieldAgentDrafts
      ? props.paginationId.fieldAgentId
      : props.paginationId.healthSystemId

  const totalPages =
    !selectorId || selectorId === SELECTOR_ID.fieldAgentDrafts
      ? props.queryData.inProgressData &&
        props.queryData.inProgressData.totalItems &&
        Math.ceil(props.queryData.inProgressData.totalItems / props.pageSize)
      : props.queryData.notificationData &&
        props.queryData.notificationData.totalItems &&
        Math.ceil(props.queryData.notificationData.totalItems / props.pageSize)

  const noContent =
    !selectorId || selectorId === SELECTOR_ID.fieldAgentDrafts
      ? transformRemoteDraftsContent(inProgressData).length <= 0
      : transformRemoteDraftsContent(notificationData).length <= 0

  const noResultMessage =
    !selectorId || selectorId === SELECTOR_ID.fieldAgentDrafts
      ? intl.formatMessage(wqMessages.noRecordsFieldAgents)
      : intl.formatMessage(wqMessages.noRecordsHealthSystem)

  const tabs = getTabs(
    selectorId,
    (inProgressData && inProgressData.totalItems) || 0,
    (notificationData && notificationData.totalItems) || 0
  )

  return (
    <WQContentWrapper
      title={intl.formatMessage(navigationMessages.progress)}
      isMobileSize={width < props.theme.grid.breakpoints.lg ? true : false}
      tabBarContent={tabs}
      isShowPagination={isShowPagination}
      paginationId={paginationId}
      totalPages={totalPages}
      onPageChange={onPageChange}
      loading={tabs ? props.loading : false}
      error={props.error}
      noResultText={noResultMessage}
      noContent={noContent}
    >
      {(!selectorId || selectorId === SELECTOR_ID.fieldAgentDrafts) &&
        renderFieldAgentTable(inProgressData, isShowPagination)}
      {selectorId === SELECTOR_ID.hospitalDrafts &&
        renderHospitalTable(notificationData, isShowPagination)}
    </WQContentWrapper>
  )
}

