function getMonthDateRange(year: number, month: number) {
  return {
    start: startOfMonth(new Date(year, month - 1)),
    end: endOfMonth(new Date(year, month - 1))
  }
}

function calculateTotalPaymentAmount(metrics: GQLPaymentMetric[]) {
  return metrics
    .map((metric) => metric.total)
    .reduce((m, metric) => m + metric, 0)
}

function getPrimaryLocationIdOfOffice(
  locations: { [key: string]: ILocation },
  office: ILocation
) {
  const location = locations[office.partOf.split('/')[1]]

  if (!location) {
    throw new Error(`Office location of ${office.id} not found`)
  }

  return location.id
}

