getStatusCountView(data: GQLRegistrationCountResult) {
    const { intl, statusMapping, selectedEvent, isAccessibleOffice } =
      this.props
    const { results, total } = data
    return (
      <ContentHolder id="declaration-statuses">
        <SubHeader>
          {intl.formatMessage(performanceMessages.registrationByStatus)}
        </SubHeader>
        <Description>
          {intl.formatMessage(
            performanceMessages.declarationCountByStatusDescription,
            {
              event: selectedEvent.toUpperCase()
            }
          )}
        </Description>
        {results
          .filter((item) => item && checkExternalValidationStatus(item.status))
          .map((statusCount, index) => {
            return (
              statusCount && (
                <StatusProgressBarWrapper key={index}>
                  <ProgressBar
                    id={`${statusCount.status.toLowerCase()}-${index}`}
                    title={intl.formatMessage(
                      statusMapping![statusCount.status as keyof IStatusMapping]
                        .labelDescriptor
                    )}
                    color={
                      statusMapping![
                        statusCount?.status as keyof IStatusMapping
                      ].color
                    }
                    totalPoints={total}
                    disabled={!isAccessibleOffice}
                    onClick={() =>
                      this.props.onClickStatusDetails(
                        statusCount.status as keyof IStatusMapping
                      )
                    }
                    currentPoints={statusCount.count}
                  />
                </StatusProgressBarWrapper>
              )
            )
          })}
      </ContentHolder>
    )
  }

function prepareChartData(
  data: GQLMonthWiseEstimationMetric[],
  time: CompletenessRateTime
) {
  return (
    data &&
    data.reduce(
      /* eslint-disable-next-line @typescript-eslint/no-explicit-any */
      (chartData: any[], dataDetails: GQLMonthWiseEstimationMetric) => {
        if (dataDetails !== null) {
          chartData.push({
            label:
              dataDetails.month === 0
                ? format(
                    new Date(dataDetails.year, dataDetails.month),
                    'MMM yyyy'
                  )
                : format(new Date(dataDetails.year, dataDetails.month), 'MMM'),
            registeredInTargetDays: dataDetails[time],
            totalRegistered: dataDetails.total,
            totalEstimate: dataDetails.estimated,
            registrationPercentage: `${Number(
              (dataDetails[time] / dataDetails.estimated) * 100
            ).toFixed(2)}%`
          })
        }
        return chartData
      },
      []
    )
  )
}

function CompletenessRatesComponent(props: ICompletenessRateProps) {
  const [base, setBase] = useState<IEstimationBase>({
    baseType: COMPLETENESS_RATE_REPORT_BASE.TIME
  })

  const location = useLocation()
  const params = useParams()

  const { intl } = props

  const { locationId, timeStart, timeEnd, time } = parse(
    location.search
  ) as unknown as ISearchParams

  const dateStart = new Date(timeStart)
  const dateEnd = new Date(timeEnd)

  return (
    <SysAdminContentWrapper
      id="reg-rates"
      isCertificatesConfigPage={true}
      hideBackground={true}
    >
      <Content
        title={intl.formatMessage(navigationMessages.completenessRates)}
        showTitleOnMobile={true}
        size={ContentSize.LARGE}
        filterContent={
          <Filter
            locationId={locationId || NATIONAL_ADMINISTRATIVE_LEVEL}
            base={base.baseType}
            time={time}
            event={params.eventType as EventType}
            dateStart={dateStart}
            dateEnd={dateEnd}
            onBaseChange={(base) => setBase({ baseType: base })}
          />
        }
      >
        <Query
          query={
            base.baseType === COMPLETENESS_RATE_REPORT_BASE.TIME
              ? FETCH_MONTH_WISE_EVENT_ESTIMATIONS
              : FETCH_LOCATION_WISE_EVENT_ESTIMATIONS
          }
          variables={{
            event: params?.eventType?.toUpperCase(),
            timeStart: timeStart,
            timeEnd: timeEnd,
            locationId:
              locationId && locationId !== NATIONAL_ADMINISTRATIVE_LEVEL
                ? locationId
                : undefined
          }}
        >
          {({ data, loading, error }) => {
            if (error) {
              return (
                <>
                  {base.baseType === COMPLETENESS_RATE_REPORT_BASE.TIME && (
                    <RegRatesLineChart loading={true} />
                  )}
                  <CompletenessDataTable
                    loading={true}
                    base={base}
                    completenessRateTime={time}
                  />
                  <GenericErrorToast />
                </>
              )
            } else {
              return (
                <>
                  {base.baseType === COMPLETENESS_RATE_REPORT_BASE.TIME && (
                    <RegRatesLineChart
                      loading={loading}
                      data={prepareChartData(
                        data && data.fetchMonthWiseEventMetrics,
                        time
                      )}
                      completenessRateTime={time}
                      eventType={params.eventType as EventType}
                    />
                  )}
                  <CompletenessDataTable
                    loading={loading}
                    base={base}
                    data={
                      data &&
                      (base.baseType === COMPLETENESS_RATE_REPORT_BASE.TIME
                        ? data.fetchMonthWiseEventMetrics
                        : data.fetchLocationWiseEventMetrics)
                    }
                    eventType={params.eventType as EventType}
                    completenessRateTime={time}
                  />
                </>
              )
            }
          }}
        </Query>
      </Content>
    </SysAdminContentWrapper>
  )
}

