function ChangeNumberView({ show, onSuccess, onClose }: IProps) {
  const [fetchUserDetailsByMobile] =
    useLazyQuery<GetUserByMobileQuery>(GET_USER_BY_MOBILE)
  const intl = useIntl()
  const [phoneNumber, setPhoneNumber] = React.useState(EMPTY_STRING)
  const [unknownError, setUnknownError] = React.useState(false)
  const [isInvalidPhoneNumber, setIsInvalidPhoneNumber] = React.useState(false)
  const userDetails = useSelector(getUserDetails)
  const language = useSelector(getLanguage)
  const [
    showDuplicateMobileErrorNotification,
    setShowDuplicateMobileErrorNotification
  ] = React.useState(false)
  const dispatch = useDispatch()
  const onChangePhoneNumber = (event: React.ChangeEvent<HTMLInputElement>) => {
    const phoneNumber = event.target.value
    setPhoneNumber(phoneNumber)
    setIsInvalidPhoneNumber(!isAValidPhoneNumberFormat(phoneNumber))
    if (showDuplicateMobileErrorNotification) {
      setShowDuplicateMobileErrorNotification(false)
    }
  }
  const restoreState = () => {
    setPhoneNumber(EMPTY_STRING)
    setIsInvalidPhoneNumber(false)
    setUnknownError(false)
    setShowDuplicateMobileErrorNotification(false)
  }
  const toggleDuplicateMobileErrorNotification = () => {
    setShowDuplicateMobileErrorNotification((prevValue) => !prevValue)
  }
  const toggleUnknownErrorNotification = () => {
    setUnknownError((prevValue) => !prevValue)
  }
  const continueButtonHandler = async (phoneNumber: string) => {
    const formattedNumber = convertToMSISDN(phoneNumber, window.config.COUNTRY)
    const { data: userData, error } = await fetchUserDetailsByMobile({
      variables: { mobile: formattedNumber }
    })
    if (error) {
      setUnknownError(true)
      return
    }
    const mobileNumberExist = userData?.getUserByMobile

    if (!mobileNumberExist) {
      const notificationEvent = NotificationEvent.CHANGE_PHONE_NUMBER

      dispatch(
        sendVerifyCode(
          [
            {
              use: language,
              family: String(userDetails?.name?.[0].familyName),
              given: [String(userDetails?.name?.[0].firstNames)]
            }
          ],
          notificationEvent,
          convertToMSISDN(phoneNumber, window.config.COUNTRY),
          String(userDetails?.email)
        )
      )
      onSuccess(phoneNumber)
    } else {
      toggleDuplicateMobileErrorNotification()
      setUnknownError(false)
    }
  }
  React.useEffect(() => {
    if (!show) {
      restoreState()
    }
  }, [show])

  return (
    <ResponsiveModal
      id="ChangePhoneNumberModal"
      show={show}
      title={intl.formatMessage(messages.changePhoneLabel)}
      actions={[
        <TertiaryButton key="cancel" id="modal_cancel" onClick={onClose}>
          {intl.formatMessage(buttonMessages.cancel)}
        </TertiaryButton>,
        <PrimaryButton
          id="continue-button"
          key="continue"
          onClick={() => {
            continueButtonHandler(phoneNumber)
          }}
          disabled={!Boolean(phoneNumber.length) || isInvalidPhoneNumber}
        >
          {intl.formatMessage(buttonMessages.continueButton)}
        </PrimaryButton>
      ]}
      handleClose={onClose}
      contentHeight={150}
      contentScrollableY={true}
    >
      <InputField
        id="phoneNumber"
        touched={true}
        required={false}
        optionalLabel=""
        error={
          isInvalidPhoneNumber
            ? intl.formatMessage(messages.phoneNumberChangeFormValidationMsg, {
                num: intl.formatMessage({
                  defaultMessage: '10',
                  id: 'phone.digit'
                }),
                start: intl.formatMessage({
                  defaultMessage: '0(4|5)',
                  description: 'Should starts with',
                  id: 'phone.start'
                })
              })
            : ''
        }
      >
        <TextInput
          id="PhoneNumber"
          type="number"
          touched={true}
          error={isInvalidPhoneNumber}
          value={phoneNumber}
          onChange={onChangePhoneNumber}
        />
      </InputField>
      {showDuplicateMobileErrorNotification && (
        <Toast
          id="duplicate-mobile-error-notification"
          type="warning"
          onClose={() => toggleDuplicateMobileErrorNotification()}
        >
          {intl.formatMessage(messages.duplicateUserMobileErrorMessege, {
            number: phoneNumber
          })}
        </Toast>
      )}
      {unknownError && (
        <Toast
          id="unknown-error-notification"
          type="warning"
          onClose={() => toggleUnknownErrorNotification()}
        >
          {intl.formatMessage(errorMessages.unknownErrorTitle)}
        </Toast>
      )}
    </ResponsiveModal>
  )
}

function VerifyCodeView({ show, onSuccess, onClose, data }: IProps) {
  const intl = useIntl()
  const { phoneNumber, email } = data
  const userDetails = useSelector(getUserDetails)
  const nonce = useSelector(getUserNonce)
  const [verifyCode, setVerifyCode] = React.useState(EMPTY_STRING)
  const [isInvalidLength, setIsInvalidLength] = React.useState(false)
  const [errorOccured, setErrorOccured] = React.useState(false)
  const dispatch = useDispatch()
  const onChangeVerifyCode = (event: React.ChangeEvent<HTMLInputElement>) => {
    const verifyCode = event.target.value
    setVerifyCode(verifyCode)
    setIsInvalidLength(verifyCode.length === 6)
  }
  const restoreState = () => {
    setVerifyCode(EMPTY_STRING)
    setIsInvalidLength(false)
    setErrorOccured(false)
  }
  const phoneChangeCompleted = () => {
    if (userDetails && phoneNumber) {
      dispatch(
        modifyUserDetails({
          ...userDetails,
          mobile: convertToMSISDN(phoneNumber, window.config.COUNTRY)
        })
      )
    }
    onSuccess()
  }

  const emailChangeCompleted = () => {
    if (userDetails) {
      dispatch(
        modifyUserDetails({
          ...userDetails,
          email: email
        })
      )
    }
    onSuccess()
  }
  React.useEffect(() => {
    if (!show) {
      restoreState()
    }
  }, [show])

  return (
    <ResponsiveModal
      id="VerifyCodeModal"
      show={show}
      title={intl.formatMessage(messages.verifyPhoneLabel)}
      actions={[
        <TertiaryButton key="cancel" id="modal_cancel" onClick={onClose}>
          {intl.formatMessage(buttonMessages.cancel)}
        </TertiaryButton>,
        <Mutation<
          ChangePasswordMutation,
          ChangePhoneMutationVariables | ChangeEmailMutationVariables
        >
          key="change-phone-mutation"
          mutation={phoneNumber ? changePhoneMutation : changeEmailMutation}
          onCompleted={
            phoneNumber ? phoneChangeCompleted : emailChangeCompleted
          }
          onError={() => setErrorOccured(true)}
        >
          {(changePhoneOrEmail) => {
            return (
              <PrimaryButton
                id="verify-button"
                key="verify"
                onClick={() => {
                  if (userDetails?.userMgntUserID) {
                    if (phoneNumber) {
                      changePhoneOrEmail({
                        variables: {
                          userId: userDetails.userMgntUserID,
                          phoneNumber: convertToMSISDN(
                            phoneNumber,
                            window.config.COUNTRY
                          ),
                          nonce: nonce,
                          verifyCode: verifyCode
                        }
                      })
                    } else if (email) {
                      changePhoneOrEmail({
                        variables: {
                          userId: userDetails.userMgntUserID,
                          email: email,
                          nonce: nonce,
                          verifyCode: verifyCode
                        }
                      })
                    }
                  }
                }}
                disabled={!Boolean(verifyCode.length) || !isInvalidLength}
              >
                {intl.formatMessage(buttonMessages.verify)}
              </PrimaryButton>
            )
          }}
        </Mutation>
      ]}
      handleClose={onClose}
      contentHeight={150}
      contentScrollableY={true}
    >
      <Message>
        {window.config.USER_NOTIFICATION_DELIVERY_METHOD === 'sms'
          ? intl.formatMessage(messages.confirmationPhoneMsg, {
              num: phoneNumber || userDetails?.mobile
            })
          : intl.formatMessage(messages.confirmationEmailMsg, {
              email: email || userDetails?.email
            })}
      </Message>
      <InputField
        id="verifyCode"
        touched={true}
        required={false}
        optionalLabel=""
        error={
          errorOccured ? intl.formatMessage(messages.incorrectVerifyCode) : ''
        }
      >
        <TextInput
          id="VerifyCode"
          type="number"
          touched={true}
          error={errorOccured}
          value={verifyCode}
          onChange={onChangeVerifyCode}
        />
      </InputField>
    </ResponsiveModal>
  )
