function formatTimeDuration(timeInSeconds: number) {
  const seconds = timeInSeconds % 60
  timeInSeconds = Math.floor(timeInSeconds / 60)
  const minutes = timeInSeconds % 60
  timeInSeconds = Math.floor(timeInSeconds / 60)
  const hours = timeInSeconds % 24
  const days = Math.floor(timeInSeconds / 24)

  return {
    days: ('0' + days).slice(-2),
    hours: ('0' + hours).slice(-2),
    minutes: ('0' + minutes).slice(-2),
    seconds: ('0' + seconds).slice(-2)
  }
}

class SubmissionController {
  private store: AppStore
  public syncRunning = false
  private syncCount = 0

  constructor(store: AppStore) {
    this.store = store
  }

  public start = () => {
    setInterval(() => {
      this.sync()
    }, INTERVAL_TIME)
  }

  private getDeclarations = () =>
    this.store.getState().declarationsState.declarations || []

  private getSubmitableDeclarations = () => {
    return this.getDeclarations().filter(
      (app: IDeclaration) =>
        app.submissionStatus &&
        ALLOWED_STATUS_FOR_RETRY.includes(app.submissionStatus as IRetryStatus)
    )
  }
  public requeueHangingDeclarations = () => {
    const now = Date.now()
    this.getDeclarations()
      .filter((app: IDeclaration) => {
        return (
          app.submissionStatus &&
          INPROGRESS_STATUS.includes(
            app.submissionStatus as IInProgressStatus
          ) &&
          app.modifiedOn &&
          differenceInMinutes(now, app.modifiedOn) > HANGING_EXPIRE_MINUTES
        )
      })
      .forEach((app: IDeclaration) => {
        const action = app.action
        if (action && isSubmissionAction(action)) {
          this.store.dispatch(
            declarationReadyForStatusChange({
              ...app,
              action
            })
          )
        }
      })
  }
  /* eslint-disable no-console */

  public sync = () => {
    const firstSync = this.syncCount === 0
    this.syncCount++
    console.debug(`[${this.syncCount}] Starting sync...`)
    if (!isNavigatorOnline() || this.syncRunning) {
      console.debug(
        `[${this.syncCount}] Sync exiting early (offline or already syncing)`
      )
      return
    }

    this.syncRunning = true

    this.requeueHangingDeclarations()
    const submitableDeclarations = this.getSubmitableDeclarations()

    if (firstSync) {
      const failedDeclarations = this.getDeclarations().filter(
        (declaration) =>
          declaration.submissionStatus === SUBMISSION_STATUS.FAILED
      )

      submitableDeclarations.push(...failedDeclarations)
      console.debug(
        `[${this.syncCount}] First sync, adding ${failedDeclarations.length} failed declarations`
      )
    }

    console.debug(
      `[${this.syncCount}] Syncing ${submitableDeclarations.length} declarations`
    )
    for (const declaration of submitableDeclarations) {
      const action = declaration.action
      if (action && isSubmissionAction(action)) {
        this.store.dispatch(
          declarationReadyForStatusChange({
            ...declaration,
            action
          })
        )
      }
    }

    this.syncRunning = false
    console.debug(`[${this.syncCount}] Finish sync.`)
  }
}

