  action: ActionType
) {
  const actionConfig: ActionConfig | undefined = configuration.actions.find(
    (ac) => ac.type === action
  )

  const annotationFields = actionConfig
    ? getActionAnnotationFields(actionConfig)
    : []

  const annotation = fieldConfigsToActionPayload(annotationFields)

  const visibleVerificationPageIds = getVisibleVerificationPageIds(
    findRecordActionPages(configuration, action),
    annotation
  )

  const visiblePageVerificationMap = visibleVerificationPageIds.reduce(
    (acc, pageId) => ({
      ...acc,
      [pageId]: true
    }),
    {}
  )

  const fieldBasedPayload = omitHiddenFields(annotationFields, annotation)

  return {
    ...fieldBasedPayload,
    ...visiblePageVerificationMap
  }
}

function getActionRequests(actionType: ActionType, actions: Action[]) {
  const filtered = actions.filter((action) => action.type === actionType)

  const accept = filtered.find(
    (action) => action.status === ActionStatus.Accepted
  )

  const request = filtered.find(
    (action) => action.status === ActionStatus.Requested
  )

  const reject = filtered.find(
    (action) => action.status === ActionStatus.Rejected
  )

  return {
    reject,
    accept,
    request
  }
}

function getDeclarationActionCreationMetadata(
  actionType: ActionType,
  actions: Action[]
) {
  const { accept: acceptAction, request: requestAction } = getActionRequests(
    actionType,
    actions
  )

  if (!acceptAction) {
    return null
  }

  const registrationNumber =
    acceptAction.type === ActionType.REGISTER
      ? (acceptAction as RegisterAction).registrationNumber
      : null

  return {
    // When 3rd party API returns 200 OK, we assume that the request was accepted, and persist single 'accepted' action.
    createdAt: requestAction?.createdAt ?? acceptAction.createdAt,
    createdBy: requestAction?.createdBy ?? acceptAction.createdBy,
    createdAtLocation:
      requestAction?.createdAtLocation ?? acceptAction.createdAtLocation,
    acceptedAt: acceptAction.createdAt,
    createdByRole: requestAction?.createdByRole ?? acceptAction.createdByRole,
    registrationNumber
  }
}

function getDeclarationActionUpdateMetadata(actions: Action[]) {
  const createAction = getOrThrow(
    actions.find((action) => action.type === ActionType.CREATE),
    `Event has no ${ActionType.CREATE} action`
  )

  return [ActionType.DECLARE, ActionType.VALIDATE, ActionType.REGISTER].reduce(
    (metadata, actionType) => {
      const { accept, request } = getActionRequests(actionType, actions)

      return {
        createdAt:
          request?.createdAt ?? accept?.createdAt ?? metadata.createdAt,
        createdBy:
          request?.createdBy ?? accept?.createdBy ?? metadata.createdBy,
        createdAtLocation:
          request?.createdAtLocation ??
          accept?.createdAtLocation ??
          metadata.createdAtLocation,
        createdByRole:
          request?.createdByRole ??
          accept?.createdByRole ??
          metadata.createdByRole
      }
    },
    {
      createdAt: createAction.createdAt,
      createdBy: createAction.createdBy,
      createdAtLocation: createAction.createdAtLocation,
      createdByRole: createAction.createdByRole
    }
  )
}

function getLegalStatuses(actions: Action[]) {
  return {
    [EventStatus.DECLARED]: getDeclarationActionCreationMetadata(
      ActionType.DECLARE,
      actions
    ),
    [EventStatus.REGISTERED]: getDeclarationActionCreationMetadata(
      ActionType.REGISTER,
      actions
    )
  }
}

function getFlagsFromActions(actions: Action[]): Flag[] {
  const sortedactions = actions.sort((a, b) =>
    a.createdAt.localeCompare(b.createdAt)
  )
  const actionStatus = sortedactions.reduce(
    (actionStatuses, { type, status }) => ({
      ...actionStatuses,
      [type]: status
    }),
    {} as Record<ActionType, ActionStatus>
  )

  const flags = Object.entries(actionStatus)
    .filter(([, status]) => status !== ActionStatus.Accepted)
    .map(([type, status]) => {
      const flag = `${type.toLowerCase()}:${status.toLowerCase()}`
      return flag satisfies Flag
    })

  const isCertificatePrinted = sortedactions.reduce<boolean>(
    (prev, { type }) => {
      if (type === ActionType.PRINT_CERTIFICATE) {
        return true
      }
      if (type === ActionType.APPROVE_CORRECTION) {
        return false
      }
      return prev
    },
    false
  )

  if (isCertificatePrinted) {
    flags.push(CustomFlags.CERTIFICATE_PRINTED)
  }

  return flags
}

function getAssignedUserFromActions(actions: Array<ActionDocument>) {
  return actions.reduce<null | string>((user, action) => {
    if (action.type === ActionType.ASSIGN) {
      return action.assignedTo
    }
    if (action.type === ActionType.UNASSIGN) {
      return null
    }

    return user
  }, null)
}

function getAcceptedActions(event: EventDocument): ActionDocument[] {
  return event.actions.filter(
    (a): a is ActionDocument => a.status === ActionStatus.Accepted
  )
}

function getCurrentEventState(event: EventDocument): EventIndex {
  const creationAction = event.actions.find(
    (action) => action.type === ActionType.CREATE
  )

  if (!creationAction) {
    throw new Error(`Event ${event.id} has no creation action`)
  }

  const acceptedActions = getAcceptedActions(event)

  const declarationUpdateMetadata = getDeclarationActionUpdateMetadata(
    event.actions
  )

  const declaration = aggregateActionDeclarations(acceptedActions)

  const dateOfEvent =
    ZodDate.safeParse(
      event.dateOfEvent?.fieldId
        ? declaration[event.dateOfEvent.fieldId]
        : event[DEFAULT_DATE_OF_EVENT_PROPERTY]
    ).data ?? null

  return deepDropNulls({
    id: event.id,
    type: event.type,
    status: getStatusFromActions(event.actions),
    legalStatuses: getLegalStatuses(event.actions),
    createdAt: creationAction.createdAt,
    createdBy: creationAction.createdBy,
    createdAtLocation: creationAction.createdAtLocation,
    updatedAt: declarationUpdateMetadata.createdAt,
    assignedTo: getAssignedUserFromActions(acceptedActions),
    updatedBy: declarationUpdateMetadata.createdBy,
    updatedAtLocation: declarationUpdateMetadata.createdAtLocation,
    declaration,
    trackingId: event.trackingId,
    // @TODO: unify this with rest of the code. It will trip us if updatedBy has different rules than updatedByUserRole
    updatedByUserRole: declarationUpdateMetadata.createdByRole,
    dateOfEvent,
    flags: getFlagsFromActions(event.actions)
  })
}

function getCurrentEventStateWithDrafts(
  event: EventDocument,
  drafts: Draft[]
): EventIndex {
  const actions = event.actions
    .slice()
    .sort((a, b) => a.createdAt.localeCompare(b.createdAt))

  const activeDrafts = findActiveDrafts(event, drafts)
    .map((draft) => draft.action)
    .flatMap((action) => {
      /*
       * If the action encountered is "REQUEST_CORRECTION", we want to pretend like it was approved
       * so previews etc are shown correctly
       */
      if (action.type === ActionType.REQUEST_CORRECTION) {
        return [
          action,
          {
            ...action,
            type: ActionType.APPROVE_CORRECTION
          }
        ] as ActionDocument[]
      }
      return [action] as ActionDocument[]
    })

  const actionWithDrafts = [...actions, ...activeDrafts].sort()
  const withDrafts: EventDocument = {
    ...event,
    actions: actionWithDrafts
  }

  return getCurrentEventState(withDrafts)
}

function applyDraftsToEventIndex(
  eventIndex: EventIndex,
  drafts: Draft[]
) {
  const indexedAt = eventIndex.updatedAt

  const activeDrafts = drafts
    .filter(({ createdAt }) => new Date(createdAt) > new Date(indexedAt))
    .map((draft) => draft.action)
    .sort()

  if (activeDrafts.length === 0) {
    return eventIndex
  }

  return {
    ...eventIndex,
    declaration: {
      ...eventIndex.declaration,
      ...activeDrafts[activeDrafts.length - 1].declaration
    }
  }
}

function getAnnotationFromDrafts(drafts: Draft[]) {
  const actions = drafts.map((draft) => draft.action)

  const annotation = actions.reduce((ann, action) => {
    return deepMerge(ann, action.annotation ?? {})
  }, {})

  return deepDropNulls(annotation)
}

