function generateTransactionId() {
  return getUUID()
}

function hasAnyOfScopes(a: Scope[], b: Scope[]) {
  return intersection(a, b).length > 0
}

function filterUnallowedActions(
  actions: ActionType[],
  userScopes: Scope[]
): ActionType[] {
  const allowedActions = actions.filter((action) => {
    const requiredScopes = ACTION_ALLOWED_SCOPES[action]

    if (requiredScopes === null) {
      return true
    }

    return hasAnyOfScopes(userScopes, requiredScopes)
  })
  // Check if the user can perform any action other than READ, ASSIGN, or UNASSIGN
  const hasOtherAllowedActions = allowedActions.some(
    (action) =>
      action !== ActionType.READ &&
      action !== ActionType.ASSIGN &&
      action !== ActionType.UNASSIGN
  )

  if (hasOtherAllowedActions) {
    return allowedActions
  }

  // If the user can only perform READ, restrict them from ASSIGN or UNASSIGN
  return [ActionType.READ]
}

function isFieldValue(
  value: FieldConfigDefaultValue
): value is FieldValue {
  return FieldValue.safeParse(value).success
}

function isFieldValueWithoutTemplates(
  value: FieldConfigDefaultValue
): value is FieldValue {
  if (isTemplateVariable(value)) {
    return false
  }

  if (
    typeof value === 'object' &&
    Object.values(value).some((val) => isTemplateVariable(val))
  ) {
    return false
  }

  return true
}

function isFieldConfigDefaultValue(
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  value: any
): value is FieldConfigDefaultValue {
  if (!value) {
    return false
  }

  if (isFieldValue(value)) {
    return true
  }

  if (isTemplateVariable(value)) {
    return true
  }

  if (
    typeof value === 'object' &&
    Object.values(value).every((v) => typeof v === 'object' && v !== null)
  ) {
    return Object.values(value).every((v) => isFieldConfigDefaultValue(v))
  }

  return false
}

function savedPractitioner(
  overrides = {} as Partial<SavedPractitioner>
) {
  return { ...defaultSavedPractitioner, ...overrides } as SavedPractitioner
