function validate(schema: JSONSchema, data: ConditionalParameters) {
  return ajv.validate(schema, data)
}

function isConditionMet(
  conditional: JSONSchema,
  values: Record<string, unknown>
) {
  return validate(conditional, {
    $form: values,
    $now: formatISO(new Date(), { representation: 'date' })
  })
}

function getConditionalActionsForField(
  field: FieldConfig,
  values: ConditionalParameters
) {
  if (!field.conditionals) {
    return []
  }
  return field.conditionals
    .filter((conditional) => validate(conditional.conditional, values))
    .map((conditional) => conditional.type)
}

function areConditionsMet(
  conditions: FieldConditional[],
  values: Record<string, unknown>
) {
  return conditions.every((condition) =>
    isConditionMet(condition.conditional, values)
  )
}

function isFieldConditionMet(
  field: FieldConfig,
  form: ActionUpdate | EventState,
  conditionalType: ConditionalType
) {
  const hasRule = (field.conditionals ?? []).some(
    (conditional) => conditional.type === conditionalType
  )

  if (!hasRule) {
    return true
  }

  const validConditionals = getConditionalActionsForField(field, {
    $form: form,
    $now: formatISO(new Date(), {
      representation: 'date'
    })
  })

  return validConditionals.includes(conditionalType)
}

function isFieldVisible(
  field: FieldConfig,
  form: ActionUpdate | EventState
) {
  return isFieldConditionMet(field, form, ConditionalType.SHOW)
}

function isFieldEmptyAndNotRequired(field: FieldConfig, form: ActionUpdate) {
  const fieldValue = form[field.id]
  return !field.required && (fieldValue === undefined || fieldValue === '')
}

function isFieldEnabled(
  field: FieldConfig,
  form: ActionUpdate | EventState
) {
  return isFieldConditionMet(field, form, ConditionalType.ENABLE)
}

function isFieldDisplayedOnReview(
  field: FieldConfig,
  form: ActionUpdate | EventState
) {
  return (
    isFieldVisible(field, form) &&
    isFieldConditionMet(field, form, ConditionalType.DISPLAY_ON_REVIEW)
  )
}

function createIntlError(message: TranslationConfig) {
  return {
    message: {
      message
    }
  }
}

function zodToIntlErrorMap(issue: ZodIssueOptionalMessage, _ctx: ErrorMapCtx) {
  // eslint-disable-next-line @typescript-eslint/switch-exhaustiveness-check
  switch (issue.code) {
    case 'invalid_string': {
      if (_ctx.data === '') {
        return createIntlError(errorMessages.requiredField)
      }

      if (issue.validation === 'date') {
        return createIntlError(errorMessages.invalidDate)
      }

      if (issue.validation === 'email') {
        return createIntlError(errorMessages.invalidEmail)
      }

      break
    }

    case 'invalid_type': {
      if (issue.expected !== issue.received && issue.received === 'undefined') {
        return createIntlError(errorMessages.requiredField)
      }

      break
    }
    case 'too_small': {
      if (issue.message === undefined) {
        return createIntlError(errorMessages.requiredField)
      }

      break
    }
    case 'invalid_union': {
      for (const { issues } of issue.unionErrors) {
        for (const e of issues) {
          if (
            zodToIntlErrorMap(e, _ctx).message.message.id !==
            'v2.error.required'
          ) {
            return createIntlError(errorMessages.invalidInput)
          }
        }
      }
      return createIntlError(errorMessages.requiredField)
    }
  }

  return createIntlError(errorMessages.invalidInput)
}

function runCustomFieldValidations({
  field,
  conditionalParameters
}: {
  field: FieldConfig
  conditionalParameters: ConditionalParameters
}) {
  return (field.validation ?? [])
    .filter((validation) => {
      return !validate(validation.validator, conditionalParameters)
    })
    .map((validation) => ({ message: validation.message }))
}

function validateFieldInput({
  field,
  value
}: {
  field: FieldConfig
  value: FieldUpdateValue
}) {
  const rawError = mapFieldTypeToZod(field.type, field.required).safeParse(
    value,
    {
      // @ts-expect-error
      errorMap: zodToIntlErrorMap
    }
  )

  // We have overridden the standard error messages
  return (rawError.error?.issues.map((issue) => issue.message) ??
    []) as unknown as {
    message: TranslationConfig
  }[]
}

function runFieldValidations({
  field,
  values
}: {
  field: FieldConfig
  values: ActionUpdate
}) {
  if (
    !isFieldVisible(field, values) ||
    isFieldEmptyAndNotRequired(field, values)
  ) {
    return {
      errors: []
    }
  }

  const conditionalParameters = {
    $form: values,
    $now: formatISO(new Date(), { representation: 'date' })
  }

  const fieldValidationResult = validateFieldInput({
    field,
    value: values[field.id]
  })

  const customValidationResults = runCustomFieldValidations({
    field,
    conditionalParameters
  })

  return {
    // Assumes that custom validation errors are based on the field type, and extend the validation.
    errors: [...fieldValidationResult, ...customValidationResults]
  }
}

function maskEmail(email: string) {
  if (email.length <= 10)
    return `${email.at(0)}${'*'.repeat(email.length - 2)}${email.at(-1)}`

  // The regex matches everything EXCEPT the first 3 and last 4 characters.
  return email.replace(/(?<=.{3}).*(?=.{4})/, (match) =>
    '*'.repeat(match.length)
  )
}

function maskSms(sms: string) {
  if (sms.length <= 8)
    return `${sms.at(0)}${'*'.repeat(sms.length - 2)}${sms.at(-1)}`

  // The regex matches everything EXCEPT the first 3 and last 2 characters.
  return sms.replace(/(?<=.{3}).*(?=.{2})/, (match) => '*'.repeat(match.length))
}

function defineConditional(schema: any) {
  return schema as JSONSchema
}

function alwaysTrue(): AjvJSONSchema {
  return {}
}

function and(...conditions: AjvJSONSchema[]): JSONSchema {
  return defineConditional({
    type: 'object',
    allOf: conditions,
    required: []
  })
}

function or(...conditions: AjvJSONSchema[]): JSONSchema {
  return defineConditional({
    type: 'object',
    anyOf: conditions,
    required: []
  })
}

function not(condition: AjvJSONSchema): JSONSchema {
  return defineConditional({
    type: 'object',
    not: condition,
    required: []
  })
}

function never(): JSONSchema {
  return not(alwaysTrue())
}

function createEventConditionals() {
  return {
    /**
     * Checks if the event contains a specific action type.
     * @param action - The action type to check for.
     */
    hasAction: (action: ActionType) =>
      defineConditional({
        type: 'object',
        properties: {
          $event: {
            type: 'object',
            properties: {
              actions: {
                type: 'array',
                contains: {
                  type: 'object',
                  properties: {
                    type: {
                      const: action
                    }
                  },
                  required: ['type']
                }
              }
            },
            required: ['actions']
          }
        },
        required: ['$event']
      })
  }
}

function getDateFromNow(days: number) {
  return new Date(Date.now() - days * 24 * 60 * 60 * 1000)
    .toISOString()
    .split('T')[0]
}

function getDateRangeToFieldReference(
  fieldId: string,
  comparedFieldId: string,
  clause: 'formatMinimum' | 'formatMaximum'
) {
  return {
    type: 'object',
    properties: {
      [fieldId]: {
        type: 'string',
        format: 'date',
        [clause]: { $data: `1/${comparedFieldId}` }
      },
      [comparedFieldId]: { type: 'string', format: 'date' }
    },
    required: [fieldId]
  }
}

function isFieldReference(value: unknown): value is FieldReference {
  return typeof value === 'object' && value !== null && '_fieldId' in value
}

function createFieldConditionals(fieldId: string) {
  const getDateRange = (
    date: string,
    clause: 'formatMinimum' | 'formatMaximum'
  ) => ({
    type: 'object',
    properties: {
      [fieldId]: {
        type: 'string',
        format: 'date',
        [clause]: date
      }
    },
    required: [fieldId]
  })

  return {
    isAfter: () => ({
      days: (days: number) => ({
        inPast: () =>
          defineFormConditional(
            getDateRange(getDateFromNow(days), 'formatMinimum')
          ),
        inFuture: () =>
          defineFormConditional(
            getDateRange(getDateFromNow(-days), 'formatMinimum')
          )
      }),
      date: (date: string | FieldReference) => {
        if (isFieldReference(date)) {
          const comparedFieldId = date._fieldId
          return defineFormConditional(
            getDateRangeToFieldReference(
              fieldId,
              comparedFieldId,
              'formatMinimum'
            )
          )
        }

        return defineFormConditional(getDateRange(date, 'formatMinimum'))
      },
      now: () =>
        defineFormConditional(getDateRange(getDateFromNow(0), 'formatMinimum'))
    }),
    isBefore: () => ({
      days: (days: number) => ({
        inPast: () =>
          defineFormConditional(
            getDateRange(getDateFromNow(days), 'formatMaximum')
          ),
        inFuture: () =>
          defineFormConditional(
            getDateRange(getDateFromNow(-days), 'formatMaximum')
          )
      }),
      date: (date: string | FieldReference) => {
        if (isFieldReference(date)) {
          const comparedFieldId = date._fieldId
          return defineFormConditional(
            getDateRangeToFieldReference(
              fieldId,
              comparedFieldId,
              'formatMaximum'
            )
          )
        }

        return defineFormConditional(getDateRange(date, 'formatMaximum'))
      },
      now: () =>
        defineFormConditional(getDateRange(getDateFromNow(0), 'formatMaximum'))
    }),

function getUUID() {
  return uuidv4() as UUID
}

function parseScope(scope: string) {
  const maybeLiteralScope = LiteralScopes.safeParse(scope)
  if (maybeLiteralScope.success) {
    return {
      type: maybeLiteralScope.data
    }
  }

  const maybeConfigurableScope = rawConfigurableScope.safeParse(scope)
  if (!maybeConfigurableScope.success) {
    return
  }

  const rawScope = maybeConfigurableScope.data
  const [, type, rawOptions] = rawScope.match(rawConfigurableScopeRegex) ?? []

  // Different options are separated by commas, and each option value is separated by a pipe e.g.:
  // record.digitise[event=v2.birth|tennis-club-membership, my-jurisdiction]
  const options = rawOptions
    .split(',')
    .reduce((acc: Record<string, string[]>, option) => {
      const [key, value] = option.split('=')
      acc[key] = value.split('|')
      return acc
    }, {})

  const parsedScope = {
    type,
    options
  }

  const result = ConfigurableScopes.safeParse(parsedScope)
  return result.success ? result.data : undefined
}

function stringifyScope(scope: ConfigurableScopes) {
  const options = Object.entries(scope.options)
    .map(([key, value]) => `${key}=${value.join('|')}`)
    .join(',')

  return `${scope.type}[${options}]`
}

function getFieldParams(form: Record<string, unknown> = DEFAULT_FORM) {
  return {
    $form: form,
    $now: formatISO(new Date(), { representation: 'date' })
  } satisfies ConditionalParameters
}

function createSearchConfig<T extends {}>(baseField: T) {
  return {
    /**
     * Creates a range configuration for the specified field.
     *
     * @returns An object containing the field ID and a configuration object with a type of 'range'.
     *
     * @example event('legalStatus.REGISTERED.createdAt').range()
     * // {
     * //   ...
     * //   config: { type: 'range' }
     * // }
     */
    range: () => ({
      ...baseField,
      config: { type: 'range' as const }
    }),
    /**
     * Creates a configuration for exact matching of the specified field.
     * @returns  An object containing the field ID and a configuration object with a type of 'exact'.
     * @example field('dob').exact()
     * // {
     * //   ...
     * //   config: { type: 'exact' }
     * // }
     */
    exact: () => ({
      ...baseField,
      config: { type: 'exact' as const }
    }),
    /**
     * Creates a configuration for fuzzy matching of the specified field.
     * @returns  An object containing the field ID and a configuration object with a type of 'exact'.
     * @example field('name').fuzzy()
     * // {
     * //   ...
     * //   config: { type: 'fuzzy' }
     * // }
     */
    fuzzy: () => ({
      ...baseField,
      config: { type: 'fuzzy' as const }
    })
  }
}

function getNetworkAddress() {
  const interfaces = networkInterfaces()
  return []
    .concat(...Object.values(interfaces))
    .filter(
      (iface: NetworkInterfaceInfo) =>
        'IPv4' === iface.family && !iface.internal
    )[0]
}

async function init() {
  const url = await ngrok.connect({
    proto: 'http', // http|tcp|tls, defaults to http
    addr: 3000, // port or network address, defaults to 80
    authtoken: process.env.AUTH_TOKEN, // your authtoken from ngrok.com
    region: 'eu'
  })

  const loginConfig = readFileSync(join(__dirname, '../login/public/config.js'))

  writeFileSync(
    join(__dirname, '../login/public/config.js'),
    loginConfig
      .toString()
      .replace(/localhost/g, iface.address)
      .replace(/(CLIENT_APP_URL: ).*,/, `$1 '${url}',`)
  )

  const clientConfig = readFileSync(
    join(__dirname, '../client/public/config.js')
  )

  writeFileSync(
    join(__dirname, '../client/public/config.js'),
    clientConfig
      .toString()
      .replace(/(API_GATEWAY_URL: ).*,/, `$1 '/gateway/',`)
      .replace(/(COUNTRY_CONFIG_URL: ).*,/, `$1 '/countryconfig',`)
  )

  console.log('Proxy is running:', url)
}

