async function resolveLocationChildren(locationId: UUID) {
  const response = await fetch(FETCH_ALL_LOCATION_CHILDREN(locationId))
  if (!response.ok) {
    throw new Error(
      "Couldn't fetch the children of the location from config: " +
        (await response.text())
    )
  }

  const locations = (await response.json()) as Array<SavedLocation>
  return locations.map((location) => location.id)
}

function generateRandomPassword(demoUser?: boolean) {
  if (!!demoUser) {
    return 'test'
  }

  const length = 6
  const charset =
    'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789'

  let randomPassword = ''
  for (let i = 0; i < length; i += 1) {
    randomPassword += charset.charAt(Math.floor(Math.random() * charset.length))
  }

  return randomPassword
}

function generateOldHash(content: string, salt: string): string {
  const hash = createHash('sha512')
  hash.update(salt)
  hash.update(content)
  return hash.digest('hex')
}

function generateHash(content: string, salt: string): string {
  return bcrypt.hashSync(content, salt)
}

function generateSaltedHash(password: string): ISaltedHash {
  const salt = bcrypt.genSaltSync(10)
  return {
    hash: generateHash(password, salt),
    salt
  }
}

async function countUsersByLocation(locationId: UUID | undefined) {
  // For the whole country
  const roles = await fetchJSON<Roles>(
    joinURL(env.COUNTRY_CONFIG_URL, '/roles')
  )
  const registrarRoles = roles
    .filter((role) => role.scopes.includes(SCOPES.RECORD_REGISTER))
    .map((role) => role.id)
  if (!locationId) {
    const resArray = await User.aggregate([
      {
        $match: {
          role: { $in: registrarRoles }
        }
      },
      { $count: 'registrars' }
    ])
    return resArray[0] ?? { registrars: 0 }
  }

  const locationChildren = await resolveLocationChildren(locationId)

  const resArray = await User.aggregate([
    {
      $match: {
        primaryOfficeId: { $in: locationChildren },
        role: { $in: registrarRoles }
      }
    },
    {
      $group: { _id: '$primaryOfficeId', registrars: { $addToSet: '$_id' } }
    },
    {
      $project: {
        _id: 0,
        registrars: { $size: '$registrars' } // Count the number of unique user ids collected in registrars
      }
    }
  ])

  return resArray[0] ?? { registrars: 0 }
}

async function countUsersByLocationHandler(request: Hapi.Request) {
  const { locationId } = request.payload as Payload
  return countUsersByLocation(locationId)
}

async function verifySecurityAnswer(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IVerifySecurityAnswer

  const user: IUserModel | null = await User.findById(payload.userId)
  if (!user) {
    logger.error(`No user details found by given userid: ${payload.userId}`)
    // Don't return a 404 as this gives away that this user account exists
    throw unauthorized()
  }

  const questionAnswers = user.securityQuestionAnswers || []

  const isCorrect = questionAnswers.some(
    (securityQNA) =>
      securityQNA.questionKey === payload.questionKey &&
      generateHash(payload.answer.toLowerCase(), user.salt) ===
        securityQNA.answerHash
  )

  if (
    !user.securityQuestionAnswers ||
    !isNonEmptyArray(user.securityQuestionAnswers)
  ) {
    logger.error(`Unable to get security questions for user: ${payload.userId}`)
    throw conflict("User doesn't have security questions")
  }

  return {
    matched: isCorrect,
    questionKey: isCorrect
      ? payload.questionKey
      : getRandomQuestionKey(user.securityQuestionAnswers, payload.questionKey)
  }
}

async function changePhoneHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const userUpdateData = request.payload as IChangePasswordPayload
  const user: IUserModel | null = await User.findById(userUpdateData.userId)
  const remoteAddress =
    request.headers['x-real-ip'] || request.info.remoteAddress
  const userAgent =
    request.headers['x-real-user-agent'] || request.headers['user-agent']
  if (!user) {
    logger.error(
      `No user details found by given userid: ${userUpdateData.userId}`
    )
    // Don't return a 404 as this gives away that this user account exists
    throw unauthorized()
  }

  if (userUpdateData.phoneNumber) {
    if (user.status !== statuses.ACTIVE) {
      logger.error(
        `User is not in active state for given userid: ${userUpdateData.userId}`
      )
      // Don't return a 404 as this gives away that this user account exists
      throw unauthorized()
    }
    user.mobile = userUpdateData.phoneNumber
  }

  try {
    await User.update({ _id: user._id }, user)
  } catch (err) {
    // return 400 if there is a validation error when updating to mongo
    return h.response(err.message).code(400)
  }
  try {
    await postUserActionToMetrics(
      'PHONE_NUMBER_CHANGED',
      request.headers.authorization,
      remoteAddress,
      userAgent
    )
  } catch (err) {
    logger.error(err)
  }
  return h.response().code(200)
}

