function getSystemIntegrationRoleScopes(
  type: SystemIntegrationRole,
  eventIds: string[]
): string[] {
  const literalScopes = DEFAULT_SYSTEM_INTEGRATION_ROLE_SCOPES[type]

  const configurableScopes =
    DEFAULT_SYSTEM_INTEGRATION_ROLE_CONFIGURABLE_SCOPES[type].map((scope) =>
      stringifyScope({
        type: scope,
        options: { event: eventIds }
      })
    )

  return [...literalScopes, ...configurableScopes]
}

async function registerSystem(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = request.payload as IRegisterSystemPayload
  const { name, type, integratingSystemType } = payload
  let { settings } = payload

  try {
    if (type === types.WEBHOOK && !settings) {
      logger.error('Webhook payloads are required !')
      return h.response('Webhook payloads are required !').code(400)
    }

    if (type === types.RECORD_SEARCH && !settings) {
      settings = {
        dailyQuota: RECORD_SEARCH_QUOTA,
        webhook: []
      }
    }

    const authorization = request.headers.authorization as string
    const token = getTokenPayload(authorization.split(' ')[1])
    const userId = token.sub
    const systemAdminUser = await User.findById(userId)
    const existingSystem = await System.findOne({ type: type })

    if (!systemAdminUser || systemAdminUser.status !== statuses.ACTIVE) {
      logger.error('active system admin user details cannot be found')
      throw unauthorized()
    }

    if (existingSystem && existingSystem.type === types.NATIONAL_ID) {
      throw new Error('System with NATIONAL_ID already exists !')
    }

    const eventConfigurations = await getEventConfigurations(authorization)
    const eventIds = eventConfigurations.map((eventConfig) => eventConfig.id)
    const systemScopes = getSystemIntegrationRoleScopes(type, eventIds)

    if (!systemScopes) {
      logger.error('scope doesnt exist')
      return h.response().code(400)
    }

    if (
      (process.env.NODE_ENV === 'development' || QA_ENV) &&
      !systemScopes.includes('demo')
    ) {
      systemScopes.push('demo')
    }

    const client_id = uuid()
    const clientSecret = uuid()
    const sha_secret = uuid()
    const { hash, salt } = generateSaltedHash(clientSecret)

    const practitioner = createFhirPractitioner(systemAdminUser, true)
    const practitionerId = await postFhir(authorization, practitioner)
    if (!practitionerId) {
      throw new Error(
        'Practitioner resource not saved correctly, practitioner ID not returned'
      )
    }

    const role = await createFhirPractitionerRole(
      systemAdminUser,
      practitionerId,
      true
    )
    const roleId = await postFhir(authorization, role)
    if (!roleId) {
      throw new Error(
        'PractitionerRole resource not saved correctly, practitionerRole ID not returned'
      )
    }

    if (type === types.WEBHOOK || type === types.RECORD_SEARCH) {
      const systemDetails = {
        client_id,
        name: name || systemAdminUser.username,
        createdBy: userId,
        username: systemAdminUser.username,
        status: statuses.ACTIVE,
        scope: systemScopes,
        practitionerId,
        secretHash: hash,
        salt,
        sha_secret,
        settings,
        type
      }
      const newSystem = await System.create(systemDetails)

      return h
        .response({
          // NOTE! Client secret is visible for only this response and then forever gone
          clientSecret,
          system: pickSystem(newSystem)
        })
        .code(201)
    }

    const systemDetails = {
      client_id,
      name: name || systemAdminUser.username,
      createdBy: userId,
      username: systemAdminUser.username,
      status: statuses.ACTIVE,
      scope: systemScopes,
      practitionerId,
      secretHash: hash,
      salt,
      sha_secret,
      type,
      integratingSystemType
    }
    const newSystem = await System.create(systemDetails)

    return h
      .response({
        // NOTE! Client secret is visible for only this response and then forever gone
        clientSecret,
        system: pickSystem(newSystem)
      })
      .code(201)
  } catch (err) {
    logger.error(err)
    // return 400 if there is a validation error when saving to mongo
    return h.response().code(400)
  }
}

async function deactivateSystem(
