      throw unauthorized()
    }
    system.status = statuses.DEACTIVATED

    const newSystem = await System.findOneAndUpdate(
      { _id: system._id },
      system,
      {
        returnDocument: 'after'
      }
    )

    return h.response(pickSystem(newSystem!)).code(200)
  } catch (err) {
    logger.error(err)
    return h.response().code(400)
  }
}

