async function findDuplicates(
  registrationComparison: Record<string, string[]>
) {
  const esClient = getOrCreateClient()
  const existingComposition = Object.fromEntries(
    Object.entries(registrationComparison).map(([key, values]) => [
      key,
      values[0]
    ])
  )
  const newComposition = Object.fromEntries(
    Object.entries(registrationComparison).map(([key, values]) => [
      key,
      values[1]
    ])
  )

  await esClient.update({
    index: getEventIndexName(),
    id: getUUID(),
    body: {
      doc: encodeEventIndex({
        id: getUUID(),
        transactionId: getUUID(),
        declaration: existingComposition
      } as unknown as EventIndex),
      doc_as_upsert: true
    },
    refresh: 'wait_for'
  })

  const results = await searchForDuplicates(
    {
      declaration: newComposition,
      // Random field values that should not affect the search
      id: getUUID(),
      type: 'birth',
      status: 'CREATED',
      createdAt: '2025-01-01',
      createdBy: 'test',
      createdAtLocation: 'test',
      updatedAtLocation: 'test',
      legalStatuses: {},
      assignedTo: 'test',
      updatedAt: '2025-01-01',
      updatedBy: 'test',
      trackingId: 'TEST12',
      updatedByUserRole: 'test',
      flags: []
    },
    DeduplicationConfig.parse(LEGACY_BIRTH_DEDUPLICATION_RULES)
  )

  return results
}

function encodeFieldId(fieldId: string) {
  return fieldId.replaceAll('.', FIELD_ID_SEPARATOR)
}

function decodeFieldId(fieldId: string) {
  return fieldId.replaceAll(FIELD_ID_SEPARATOR, '.')
}

function encodeEventIndex(event: EventIndex): EncodedEventIndex {
  return {
    ...event,
    declaration: Object.entries(event.declaration).reduce(
      (acc, [key, value]) => ({
        ...acc,
        [encodeFieldId(key)]: value
      }),
      {}
    )
  }
}

function decodeEventIndex(event: EncodedEventIndex): EventIndex {
  return {
    ...event,
    declaration: Object.entries(event.declaration).reduce(
      (acc, [key, value]) => ({
        ...acc,
        [decodeFieldId(key)]: value
      }),
      {}
    )
  }
}

function declarationReference(fieldName: string) {
  return `declaration.${fieldName}`
}

