async function notifyCountryConfig(
  templateName: {
    email?: string
    sms?: string
  },
  recipient: {
    email?: string | null
    sms?: string | null
    bcc?: string[]
  },
  type: 'user' | 'informant',
  variables: Record<string, unknown>,
  locale: string,
  convertUnicode?: boolean
) {
  const url = `${COUNTRY_CONFIG_URL}/notification`
  try {
    logger.info(
      `Sending notification to countryconfig.

Template: ${JSON.stringify(templateName, null, 4)}
${recipient.email ? `Email: ${maskEmail(recipient.email)}` : ''}
${recipient.sms ? `SMS: ${maskSms(recipient.sms)}` : ''}
${recipient.bcc ? `Amount of recipients: ${recipient.bcc.length}` : ''}`
    )

    return await fetch(url, {
      method: 'POST',
      body: JSON.stringify({
        templateName,
        recipient,
        type,
        locale,
        variables,
        convertUnicode
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
  } catch (error) {
    logger.error(error)
    throw error
  }
}

