function getEventType(bundle: SavedBundle) {
  const task = getTaskFromSavedBundle(bundle)
  if (!task) {
    throw new Error('No task found')
  }
  const type = task.code!.coding![0].code as EVENT_TYPE
  return type
}

function findTaskExtension<
  T extends keyof KnownExtensionType,
  Task extends TaskHistory | SavedTask
>(task: Task, extensionUrl: T) {
  return task.extension.find(
    (ext): ext is KnownExtensionType[T] => ext.url === extensionUrl
  )
}

function findExtension<T extends keyof KnownExtensionType>(
  url: T,
  extensions: Extension[] | undefined
) {
  const extension =
    extensions &&
    extensions.find((obj): obj is KnownExtensionType[T] => {
      return obj.url === url
    })
  return extension
}

async function compareForBirthDuplication(
  registrationComparison: ComparisonObject<
    Omit<BirthDocument, 'compositionId'>
  >,
  client: elasticsearch.Client
) {
  const existingComposition = Object.fromEntries(
    Object.entries<Values<BirthDocument>[]>(registrationComparison).map(
      ([key, values]) => [key, values[0]]
    )
  )
  const newComposition = Object.fromEntries(
    Object.entries<Values<BirthDocument>[]>(registrationComparison).map(
      ([key, values]) => [key, values[1]]
    )
  )

  await indexComposition(
    '123-123-123-123',
    { compositionId: '123-123-123-123', ...existingComposition },
    client
  )
  const results = await searchForBirthDuplicates(newComposition, client)
  return results
}

