async function deduplicateHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const client = getOrCreateClient()
  try {
    const payload = request.payload as fhir.Bundle
    const composition = payload.entry?.find(
      (e) => e.resource?.resourceType === 'Composition'
    )

    await removeDuplicate(
      composition?.resource as fhir.Composition & { id: string },
      client
    )
  } catch (error) {
    logger.error(`Search/searchDeclarationHandler: error: ${error}`)
    return internal(error)
  }
  return h.response().code(200)
}

async function recordHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const record = request.payload as ValidRecord

  switch (getEventType(record)) {
    case 'BIRTH':
      await upsertBirthEvent(record)
      break
    case 'DEATH':
      await upsertDeathEvent(record)
      break
    case 'MARRIAGE':
      await upsertMarriageEvent(record)
      break
    default:
      throw new Error('Unsupported event type')
  }

  return h.response().code(200)
}

