async function updateAliases() {
  const client = getOrCreateClient()

  const indices =
    (await client.cat.indices({
      format: 'json',
      index: `${OPENCRVS_INDEX_NAME}-*`
    })) ?? []

  const sortedIndices = orderBy(indices, (index) => index.index, 'desc')

  const latestIndexName = sortedIndices[0]?.index

  if (!latestIndexName) {
    logger.error('No indices found. Skipping alias update')
    return
  }

  await client.indices.updateAliases({
    actions: [
      {
        remove: {
          alias: OPENCRVS_INDEX_NAME,
          index: `${OPENCRVS_INDEX_NAME}-*`
        }
      },
      { add: { alias: OPENCRVS_INDEX_NAME, index: latestIndexName } }
    ]
  })
}

async function backupLegacyIndex() {
  logger.info(`Checking if ${OPENCRVS_INDEX_NAME} index exists...`)
  const client = getOrCreateClient()

  const ocrvsIndexExists = await client.indices.exists({
    index: OPENCRVS_INDEX_NAME
  })

  const ocrvsIndexAliasExists = await client.indices.existsAlias({
    name: OPENCRVS_INDEX_NAME
  })

  // indices.exists() returns true if the index exists or if the alias exists
  const hasLegacyIndex = ocrvsIndexExists && !ocrvsIndexAliasExists

  if (hasLegacyIndex) {
    // Since the approach is not atomic, we create backup index with a timestamped name.
    // If the actual reindexing from mongodb goes through, this will be removed
    const timestampedBackupIndexName = `${formatIndexName()}-legacy-backup`

    logger.info(
      `${OPENCRVS_INDEX_NAME} index exists, creating a copy as ${timestampedBackupIndexName}`
    )

    await client.indices.putSettings({
      index: OPENCRVS_INDEX_NAME,
      settings: {
        'index.blocks.write': true
      }
    })

    await client.indices.clone({
      index: OPENCRVS_INDEX_NAME,
      target: timestampedBackupIndexName
    })

    logger.info(`Deleting ${OPENCRVS_INDEX_NAME} index`)

    await client.indices.delete({ index: OPENCRVS_INDEX_NAME })
  } else {
    logger.info(`${OPENCRVS_INDEX_NAME} index does not exist`)
  }
}

async function formatSearchParams(
  searchPayload: ISearchCriteria,
  isExternalSearch: boolean
) {
  const {
    createdBy = '',
    from = 0,
    size = DEFAULT_SIZE,
    sortColumn,
    sortBy,
    parameters
  } = searchPayload

  const sort = []

  if (sortBy) {
    sort.push(...sortBy)
  } else if (sortColumn) {
    sort.push({
      [sortColumn === 'name' ? 'name.keyword' : sortColumn]: {
        order: searchPayload.sort ?? SortOrder.ASC,
        unmapped_type: 'keyword'
      }
    })
  }
  const query = await advancedQueryBuilder(
    parameters,
    createdBy,
    isExternalSearch
  )

  return {
    index: OPENCRVS_INDEX_NAME,
    from,
    size,
    query,
    sort
  } satisfies SearchRequest
}

async function reindexHandler(
  _request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const jobId = uuid()

  process.nextTick(async () => {
    try {
      indexingStatuses[jobId] = IndexingStatus.started

      await backupLegacyIndex()
      await reindex()
      await updateAliases()
      await prune()
      indexingStatuses[jobId] = IndexingStatus.completed
    } catch (error) {
      logger.error(error)
      indexingStatuses[jobId] = IndexingStatus.error
    }
  })

  return h
    .response({
      message: `ElasticSearch reindexing started for job ${jobId}`,
      status: indexingStatuses[jobId] ?? IndexingStatus.accepted,
      jobId
    })
    .code(202)
}

async function reindexStatusHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { jobId } = request.params as { jobId: string }

  const status = indexingStatuses[jobId]

  if (!status) {
    return h
      .response({
        message: 'ElasticSearch reindexing timestamp not found'
      })
      .code(404)
  }

  if (status === 'error') {
    return h
      .response({
        message:
          'Error when reindexing. Refer to search-service logs for details.',
        status
      })
      .code(500)
  }

  if (status === 'completed') {
    return h
      .response({
        message: `Reindexing for job ${jobId} completed`,
        status
      })
      .code(200)
  }

  return h
    .response({
      message: `ElasticSearch reindexing started for job ${jobId}`,
      status: indexingStatuses[jobId]
    })
    .code(200)
}

