    rootResource: Record<string, any>
  ) {
    for (const key of Object.keys(object)) {
      if (EXCLUDED_PATHS.includes(path + '.' + key)) {
        continue
      }
      const value = object[key]
      if (typeof value === 'string') {
        const collectionReference = /^[A-Z][a-z]+\/.*/
        if (collectionReference.test(value)) {
          if (EXCLUDED_REFERENCES.includes(value)) {
            continue
          }
          const id = value.split('/')[1]
          try {
            getFromBundleById(bundle, id)
          } catch (error) {
            const dumpFile = join(os.tmpdir(), Date.now() + '.json')
            writeFileSync(dumpFile, JSON.stringify(bundle))
            developmentTimeError(
              'Unresolved reference found: ' + value,
              'Make sure to add a join to getFHIRBundleWithRecordID query so that all resources of the records are returned',
              `Resource path: ${path}`,
              'Bundle',
              dumpFile
            )
            throw error
          }
        }
      } else if (Array.isArray(value)) {
        for (const item of value) {
          if (typeof item === 'object') {
            check(item, path + '.' + key, rootResource)
          }
        }
      } else if (typeof value === 'object') {
        check(value, path + '.' + key, rootResource)
      }
    }
  }

async function resolveLocationChildren(locationId: UUID) {
  const response = await fetch(FETCH_ALL_LOCATION_CHILDREN(locationId))
  if (!response.ok) {
    throw new Error(
      "Couldn't fetch the children of the location from config: " +
        (await response.text())
    )
  }

  const locations = (await response.json()) as Array<SavedLocation>
  return locations.map((location) => location.id)
}

async function searchAssignment(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const client = getOrCreateClient()

  const payload = request.payload as IAssignmentPayload
  try {
    const results = await searchByCompositionId(payload.compositionId, client)

    const result = results?.body?.hits?.hits[0]?._source as
      | SearchDocument
      | undefined
    return h
      .response({ practitionerId: result?.assignment?.practitionerId })
      .code(200)
  } catch (error) {
    logger.error(`Search/searchAssginment: ${error}`)
    return internal(error)
  }
}

async function getAllDocumentsHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const client = getOrCreateClient()

  try {
    // Before retrieving all documents, we need to check the total count to make sure that the query will no tbe too large
    // By performing the search, requesting only the first 10 in DEFAULT_SIZE we can get the total count
    const allDocumentsCountCheck = await client.search(
      {
        index: OPENCRVS_INDEX_NAME,
        body: {
          query: { match_all: {} },
          sort: [{ dateOfDeclaration: 'asc' }],
          size: DEFAULT_SIZE
        }
      },
      {
        meta: true,
        ignore: [404]
      }
    )

    const count = getSearchTotalCount(allDocumentsCountCheck?.body?.hits?.total)
    if (count > 5000) {
      return internal(
        'Elastic contains over 5000 results.  It is risky to return all without pagination.'
      )
    }
    // If total count is less than 5000, then proceed.
    const allDocuments = await client.search(
      {
        index: OPENCRVS_INDEX_NAME,
        body: {
          query: { match_all: {} },
          sort: [{ dateOfDeclaration: 'asc' }],
          size: count
        }
      },
      {
        ignore: [404],
        meta: true
      }
    )
    return h.response(allDocuments).code(200)
  } catch (err) {
    return internal(err)
  }
}

async function advancedQueryBuilder(
  params: IAdvancedSearchParam,
  createdBy: string,
  isExternalSearch: boolean
) {
  params = transformDeprecatedParamsToSupported(params)

  const must: QueryDslQueryContainer[] = []
  // filter is used for "pure" filtering, without caring about scores
  const filter: QueryDslQueryContainer[] = []

  if (params.name) {
    must.push({
      bool: {
        should: [
          {
            bool: {
              filter: { term: { event: 'birth' } },
              must: {
                multi_match: {
                  query: params.name,
                  fields: [
                    'name^3',
                    'childFirstNames^2',
                    'childFamilyName',
                    'informantFirstNames',
                    'informantFamilyName',
                    'motherFirstNames',
                    'motherFamilyName',
                    'fatherFirstNames',
                    'fatherFamilyName'
                  ],
                  fuzziness: 'AUTO'
                }
              }
            }
          },
          {
            bool: {
              filter: { term: { event: 'death' } },
              must: {
                multi_match: {
                  query: params.name,
                  fields: [
                    'name^3',
                    'deceasedFirstNames^2',
                    'deceasedFamilyName',
                    'informantFirstNames',
                    'informantFamilyName',
                    'spouseFirstNames',
                    'spouseFamilyName'
                  ],
                  fuzziness: 'AUTO'
                }
              }
            }
          },
          {
            bool: {
              filter: { term: { event: 'marriage' } },
              must: {
                multi_match: {
                  query: params.name,
                  fields: [
                    'brideFirstNames^6',
                    'brideFamilyName^6',
                    'groomFirstNames^6',
                    'groomFamilyName^6',
                    'witnessOneFirstNames',
                    'witnessOneFamilyName',
                    'witnessTwoFirstNames',
                    'witnessTwoFamilyName'
                  ],
                  fuzziness: 'AUTO'
                }
              }
            }
          }
        ],
        minimum_should_match: 1
      }
    })
  }

  if (params.event && params.event.length > 0) {
    const shouldMatch: QueryDslQueryContainer[] = []
    for (const { eventName, jurisdictionId } of params.event) {
      if (jurisdictionId) {
        const leafLevelJurisdictionIds = await resolveLocationChildren(
          jurisdictionId as UUID
        )
        shouldMatch.push({
          bool: {
            must: [
              {
                term: {
                  'event.keyword': eventName
                }
              },
              {
                terms: {
                  'declarationJurisdictionIds.keyword': leafLevelJurisdictionIds
                }
              }
            ]
          }
        })
      } else {
        shouldMatch.push({
          term: {
            'event.keyword': eventName
          }
        })
      }
    }

    filter.push({
      bool: {
        should: shouldMatch
      }
    })
  }

  if (
    (params.registrationStatuses && params.registrationStatuses.length > 0) ||
    isExternalSearch
  ) {
    must.push({
      query_string: {
        default_field: 'type',
        query: isExternalSearch
          ? `(${REGISTERED_STATUS}) OR (${CERTIFIED_STATUS}) OR (${ISSUED_STATUS})`
          : `(${params.registrationStatuses!.join(') OR (')})`
      }
    })
  }

  if (
    !params.dateOfEventStart &&
    !params.dateOfEventEnd &&
    params.dateOfEvent
  ) {
    must.push({
      match: {
        deathDate: params.dateOfEvent
      }
    })
  }

  if (params.dateOfEventStart || params.dateOfEventEnd) {
    if (!params.dateOfEventStart) {
      throw new Error(
        'dateOfEventStart must be provided along with dateOfEventEnd'
      )
    }
    if (!params.dateOfEventEnd) {
      throw new Error(
        'dateOfEventEnd must be provided along with dateOfEventStart'
      )
    }

    must.push({
      range: {
        deathDate: {
          gte: params.dateOfEventStart,
          lte: params.dateOfEventEnd
        }
      }
    })
  }

  if (
    !params.dateOfRegistrationStart &&
    !params.dateOfRegistrationEnd &&
    params.dateOfRegistration
  ) {
    must.push({
      match: {
        dateOfDeclaration: params.dateOfRegistration
      }
    })
  }

  if (params.dateOfRegistrationStart || params.dateOfRegistrationEnd) {
    if (!params.dateOfRegistrationStart) {
      throw new Error(
        'dateOfRegistrationStart  must be provided along with dateOfRegistrationEnd'
      )
    }

    if (!params.dateOfRegistrationEnd) {
      throw new Error(
        'dateOfRegistrationEnd  must be provided along with dateOfRegistrationStart'
      )
    }

    must.push({
      range: {
        dateOfDeclaration: {
