function signFileUrl(uri: string) {
  return presignSignatureV4(
    {
      headers: {
        host: MINIO_URL
      },
      protocol: MINIO_PROTOCOL,
      method: 'GET',
      path: uri
    },
    MINIO_ACCESS_KEY,
    MINIO_SECRET_KEY,
    undefined,
    MINIO_BUCKET_REGION,
    new Date(),
    MINIO_PRESIGNED_URL_EXPIRY_IN_SECOND
  )
}

async function defaultMinioBucketExists() {
  return minioClient.bucketExists(MINIO_BUCKET)
}

async function createDefaultMinioBucket() {
  const policy = `
    {
        "Version": "2012-10-17",
        "Statement": [
            {
            "Action": [
                "s3:GetObject"
            ],
            "Effect": "Allow",
            "Principal": {
                "AWS": [
                "*"
                ]
            },
            "Resource": [
                "arn:aws:s3:::${MINIO_BUCKET}/*"
            ],
            "Sid": ""
            }
        ]
    }
    `

  await minioClient.makeBucket(MINIO_BUCKET, MINIO_BUCKET_REGION)
  return minioClient.setBucketPolicy(MINIO_BUCKET, policy)
}

function createPreSignedUrl(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const fileUri = request.params.fileUri
  const payload = (
    fileUri ? { fileUri: `/${MINIO_BUCKET}/${fileUri}` } : request.payload
  ) as {
    fileUri: string
  }

  try {
    const presignedURL = signFileUrl(payload.fileUri)
    return h.response({ presignedURL }).code(200)
  } catch (error) {
    return h.response(error).code(400)
  }
}

function createPresignedUrlsInBulk(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const payload = BulkPayload.safeParse(request.payload)

  if (!payload.success) {
    return h.response(payload.error).code(400)
  }

  const response = payload.data.filenames.map((filename) =>
    signFileUrl(`/${MINIO_BUCKET}/${filename}`)
  )

  return h.response(response).code(200)
}

async function deleteDocument(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const filename = request.params.filename

  const userId = getUserId(request.headers.authorization)

  if (!userId)
    return Promise.reject(
      new Error(
        `request failed: Authorization token is missing or does not contain a valid user ID.`
      )
    )

  const stat = await minioClient.statObject(MINIO_BUCKET, filename)
  const createdBy = stat.metaData['created-by']

  if (createdBy !== userId)
    return h
      .response(
        `request failed: user with id ${userId} does not have permission to delete this document`
      )
      .code(403)
  await minioClient.removeObject(MINIO_BUCKET, filename)

  return h.response().code(204)
}

async function fileUploadHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const userId = getUserId(request.headers.authorization)
  const payload = await Payload.parseAsync(request.payload).catch((error) => {
    logger.error(error)
    throw badRequest('Invalid payload')
  })

  const { file, transactionId } = payload

  const extension = file.hapi.filename.split('.').pop()
  const filename = `${transactionId}.${extension}`

  await minioClient.putObject(MINIO_BUCKET, filename, file, {
    'created-by': userId
  })

  return filename
}

async function fileExistsHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const { filename } = request.params
  const exists = await minioClient.statObject(MINIO_BUCKET, filename)
  if (!exists) {
    return notFound('File not found')
  }
  return h.response().code(200)
}

async function documentUploadHandler(
  request: Hapi.Request,
  h: Hapi.ResponseToolkit
) {
  const userId = getUserId(request.headers.authorization)
  if (!userId)
    return Promise.reject(
      new Error(
        `request failed: Authorization token is missing or does not contain a valid user ID.`
      )
    )
  const payload = request.payload as IDocumentPayload
  const ref = uuid()
  try {
    const base64String = payload.fileData.split(',')[1]
    const base64Decoded = Buffer.from(base64String, 'base64')
    const fileType = (await fromBuffer(base64Decoded)) as IFileInfo
    const generateFileName = `${ref}.${fileType.ext}`

    await minioClient.putObject(MINIO_BUCKET, generateFileName, base64Decoded, {
      ...payload.metaData,
      'content-type': fileType.mime,
      'created-by': userId
    })

    return h
      .response({ refUrl: `/${MINIO_BUCKET}/${generateFileName}` })
      .code(200)
  } catch (error) {
    return Promise.reject(new Error(`request failed: ${error.message}`))
  }
}
